import React, { useState, useEffect } from "react"
import { useHistory, useParams } from "react-router-dom"
import "../assets/css/layout.css"
import { Nav, TabContent } from "react-bootstrap"
import Data from "../Data"
import Breadcrumb from "../components/common/breadcrumb"
import PagnationTool from "../PagnationTool"
import one from "../assets/images/product/1.png"
import axios from "axios"
import Cookie from "js-cookie"
const Guidelist = () => {
  const history = useHistory()
  let [tab, settab] = useState(0)
  // const [en, seten] = useState(false);
  // const [zn, setzn] = useState(false);
  // // const 영어관리스위치 = () => {
  //   seten(!en);
  // };
  return (
    <>
      <Breadcrumb title="가이드 관리" parent="가이드 관리" />
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm-12">
            <div className="card">
              <div className="card-header">
                <div className="card-body">
                  <div className="nav-sub">
                    <Nav
                      variant="tabs"
                      defaultActiveKey="link-0"
                      className="nav--sub2"
                    >
                      <div className="navtab">
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-0"
                            onClick={() => {
                              settab(0)
                            }}
                          >
                            승인 상태
                          </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-1"
                            onClick={() => {
                              settab(1)
                            }}
                          >
                            활동중
                          </Nav.Link>
                        </Nav.Item>
                      </div>
                      <div>
                        {/* <button
                          id="maching-btn"
                          className="btn btn-danger btn-sm"
                          onClick={() => {
                            history.push('/recomendguidecnt');
                          }}
                        >
                          상품연결
                        </button> */}
                      </div>
                    </Nav>
                    <TabContent2 tab={tab} />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* <div className="card-block row">
        <div className="col-sm-12 col-lg-12 col-xl-12"></div>
      </div> */}
    </>
  )
}
function TabContent2(props) {
  if (props.tab === 0) {
    return (
      <div>
        <GuideSome />
      </div>
    )
  } else if (props.tab === 1) {
    return (
      <div>
        <GuideOk />
      </div>
    )
  }
}

const GuideSome = () => {
  const history = useHistory()
  const [waitlsit, setWaitlsit] = useState([])
  const [searchText, setSearchText] = useState()

  useEffect(() => {
    let form = new FormData()
    form.append("index", 1)
    form.append("type", 1)
    axios
      .post("/api/v1/guide/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        console.log(response)
        if (response.data.status === "success") {
          setWaitlsit(response.data.data.list)
        }
      })
      .catch(err => {
        alert("가이드리스트를  불러오는데 실패했습니다.")
      })
  }, [])
  const onSearchHandler = () => {
    const form = new FormData()
    form.append("index", 1)
    form.append("type", 1)
