import React, { useState, useEffect } from "react"
import "../assets/css/layout.css"
import Breadcrumb from "../components/common/breadcrumb"
import { Nav } from "react-bootstrap"
import axios from "axios"
import Cookie from "js-cookie"
import SweetAlert from "react-bootstrap-sweetalert"
const Hashtag = () => {
  const [en, seten] = useState(0)

  return (
    <>
      <Breadcrumb title="해시태그관리" parent="해시태그관리" />
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm-12">
            <div className="card">
              <div className="card-header">
                <div className="bennerContent">
                  <h5>검색 해시태그</h5>
                </div>

                <div className="card-body">
                  <Nav
                    variant="tabs"
                    defaultActiveKey="link-0"
                    className="nav--sub2"
                  >
                    <div className="navtab">
                      <Nav.Item>
                        <Nav.Link
                          eventKey="link-0"
                          onClick={() => {
                            seten(0)
                          }}
                        >
                          영문관리
                        </Nav.Link>
                      </Nav.Item>
                      <Nav.Item>
                        <Nav.Link
                          eventKey="link-1"
                          onClick={() => {
                            seten(1)
                          }}
                        >
                          중문관리
                        </Nav.Link>
                      </Nav.Item>
                    </div>
                  </Nav>
                </div>
                <TabContentTab en={en} />
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}
function TabContentTab(props) {
  if (props.en === 0) {
    return (
      <div className="tag-tab-box">
        <Enup />
      </div>
    )
  } else if (props.en === 1) {
    return (
      <div className="tag-tab-box">
        <Znup />
      </div>
    )
  } else if (props.누른탭 === 2) {
    return <div>내용2</div>
  }
}
const Znup = () => {
  const [zhTagList, setZhTaglist] = useState([])
  const [usertext, setusertxt] = useState("")
  const [isShow, setIsShow] = useState(false)
  useEffect(() => {
    let form = new FormData()
    form.append("lang", 2)
    axios
      .post("/api/v1/hashtag/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        if (response.data.status === "success") {
          setZhTaglist(response.data.data)
          console.log(response)
        }
      })
      .catch(err => {
        alert("태그를 불러오는데 실패했습니다.")
      })
  }, [])

  const addClick = async e => {
    if (!usertext) {
      alert("한글자이상입력하세요")
      return
    }
    const index = zhTagList.findIndex(i => i.name === usertext)
    if (index >= 0) {
      alert("이미 추가된 태그 입니다.")
      return
    }
    const arrayCopy = [...zhTagList, { name: usertext }]

    setZhTaglist(arrayCopy)
    setusertxt("")
    let form = new FormData()

    form.append("lang", 2)
    form.append("name", usertext)

    const cookie = Cookie.get("token")

    try {
      const { data } = await axios.post("/api/v1/hashtag/add", form, {
        headers: {
          Authorization: cookie
        }
      })
      if (data.status == "success") {
        alert("성공적으로 작성되었습니다.")
      }
    } catch (error) {
      alert("작성하는데 실패했습니다.")
    }
  }

  const deleteList = (listNo, index) => {
    if (window.confirm("삭제 하시겠습니까?")) {
      let form = new FormData()
      form.append("rec_no", listNo)
      axios
        .post("/api/v1/hashtag/delete", form, {
          headers: { Authorization: Cookie.get("token") }
        })
        .then(response => {
          const list = zhTagList.filter((item, i) => i !== index)
          console.log(list)
          setZhTaglist(list)
        })
    }
  }

  return (
    <>
      <div className="tag-total">
        <div className="teg-text">해시태그추가</div>
        &nbsp;
        <div className="tag-add-box">
          <input
            className="form-control"
            placeholder="중문입력"
            onChange={e => {
              setusertxt(e.target.value)
            }}
            value={usertext}
            onKeyDown={e => {
              if (e.key === "Enter") {
                addClick()
              }
            }}
          />
        </div>{" "}
        &nbsp;
        <div className="tag-btn-btn">
          <button className="btn btn-secondary btn-sm" onClick={addClick}>
            추가
          </button>
        </div>
      </div>

      <div class="container">
        <div className="row">
