import React, { useState, useRef, useCallback } from "react"
import "../assets/css/layout.css"
import { useHistory, useParams } from "react-router-dom"
import Breadcrumb from "../components/common/breadcrumb"
import axios from "axios"
import Cookie from "js-cookie"
import "react-dropzone-uploader/dist/styles.css"

import "codemirror/lib/codemirror.css"
import "@toast-ui/editor/dist/toastui-editor.css"
import { Editor } from "@toast-ui/react-editor"
import DropEn from "./DropEn"
import DropZh from "./DropZn"
const AddBenner = () => {
  const [EnImageNumber, setEnImageNumber] = useState(0)
  const [ZhImageNumber, setZhImageNumber] = useState(0)
  const [ViewLank, setViewLank] = useState(0)

  const history = useHistory()
  const enEediterContens = useRef()
  const zhEditerContens = useRef()

  const enImageNumber = useCallback((number) => {
    console.log(number)
    setEnImageNumber(number)
  }, [])

  const zhImageNumber = useCallback((number) => {
    console.log(number)
    setZhImageNumber(number)
  }, [])

  const ZhText = () => {
    return (
      <div className="col-sm-12">
        <div className="card">
          <div className="card-header">
            <h5>중문내용입력하기</h5>
          </div>
          <div className="card-body">
            {/* <CKEditors
editor={CKEditors}
content={Zheditor}
events={{
change: onZhContentHandler,
}}
/> */}
            <Editor
              initialValue="니하오!"
              previewStyle="vertical"
              height="400px"
              ref={zhEditerContens}
              initialEditType="wysiwyg"
              // useCommandShortcut={true}
            />
          </div>
        </div>
      </div>
    )
  }
  const onSubmithandler = async (e) => {
    e.preventDefault()
    const Zheditor = zhEditerContens.current.getInstance().getHtml()
    const Eneditor = enEediterContens.current.getInstance().getHtml()
    let form = new FormData()

    form.append("z_index", ViewLank)
    form.append("en_image", EnImageNumber)
    form.append("zh_image", ZhImageNumber)
    form.append("zh_editor", Zheditor)
    form.append("en_editor", Eneditor)
    const cookie = Cookie.get("token")
    try {
      const { data } = await axios.post("/api/v1/banner/add", form, {
        headers: {
          Authorization: cookie,
        },
      })
      if (data.status == "success") {
        alert("성공적으로 작성되었습니다.")
        history.push("/bennerlist")
      }
    } catch (error) {
      alert("작성하는데 실패했습니다.")
    }
  }
  const EnText = () => {
    // console.log("????")
    return (
      <div className="col-sm-12">
        <div className="card">
          <div className="card-header">
            <h5>영문내용입력하기</h5>
          </div>
          <div className="card-body">
            <Editor
              initialValue="hello react editor world!"
              previewStyle="vertical"
              height="400px"
              ref={enEediterContens}
              initialEditType="wysiwyg"
              // useCommandShortcut={true}
            />
          </div>
        </div>
      </div>
    )
  }
  return (
    <>
      <Breadcrumb title="배너추가" parent="배너관리" />
      <div className="card">
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="card-body">
              <form>
                <div>
                  <div className="col-sm-12">
                    <div className="card">
                      <div className="card-header">
                        <h5>노출 우선 순위</h5>
                      </div>
                      <div className="card-body">
                        <input
                          className=" form-control"
                          type="number"
                          min="1"
                          max="999"
                          defaultValue="0"
                          onChange={(e) => setViewLank(e.target.value)}
                        />
                      </div>
                    </div>
                  </div>
                </div>
                <div>
                  <DropEn enImageNumber={enImageNumber} />
                </div>
                <div>
                  <DropZh zhImageNumber={zhImageNumber} />
                </div>
                <div>{EnText()}</div>
                <div>{ZhText()}</div>
                <div style={{ marginLeft: "40px" }} className="btn-box-bner">
                  <button
                    className="btn btn-square btn-warning btn-sm"
                    type="button"
                  >
                    삭제
                  </button>
                  &nbsp; &nbsp;
                  {/* <button className="edit-button">수정</button>{' '} */}
                  <button
                    className="btn btn-square btn-secondary active btn-sm"
                    type="button"
                    onClick={onSubmithandler}
                  >
                    작성
                  </button>
                </div>
                {/* </div> */}
              </form>
            </div>
            {/* <div className="col-sm-12">
<div> */}
            {/* </div>
</div> */}
            {/* <div className="col-sm-12"> */}
            {/* </div>
<div className="col-sm-12">
<div> */}
          </div>
        </div>
      </div>
      {/* </div>
</div> */}
    </>
  )
}
export default AddBenner
