import React, { useState, useEffect } from "react"
import { useHistory, useParams } from "react-router-dom"
import "../assets/css/layout.css"
import { Nav, TabContent } from "react-bootstrap"
import Data from "../Data"
import Breadcrumb from "../components/common/breadcrumb"
import PagnationTool from "../PagnationTool"
import one from "../assets/images/product/1.png"
import axios from "axios"
import Cookie from "js-cookie"
import Pagenation from "../util/Pagenation"
const GorpGoodsList = () => {
  const history = useHistory()
  let [tab, settab] = useState(0)

  return (
    <>
      <Breadcrumb title="단체투어리스트" parent="단체투어상품관리" />
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm-12">
            <div className="card">
              <div className="card-header">
                <div className="card-body">
                  <div className="nav-sub">
                    <Nav
                      variant="tabs"
                      defaultActiveKey="link-0"
                      className="nav--sub2"
                    >
                      <div className="navtab">
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-0"
                            onClick={() => {
                              settab(0)
                            }}
                          >
                            승인 상태
                          </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-1"
                            onClick={() => {
                              settab(1)
                            }}
                          >
                            판매상태
                          </Nav.Link>
                        </Nav.Item>
                      </div>
                      <div>
                        {/* <button
                          id="maching-btn"
                          className="btn btn-danger btn-sm"
                          onClick={() => {
                            history.push('/recomendguidecnt');
                          }}
                        >
                          상품연결
                        </button> */}
                      </div>
                    </Nav>
                    <TabContent2 tab={tab} />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

function GuideSome() {
  const [seachtext, setserchText] = useState("")

  const [waitlsit, setWaitlsit] = useState({})

  const history = useHistory()

  useEffect(() => {
    let form = new FormData()
    form.append("index", 1)
    form.append("type", 1)
    form.append("search", seachtext)
    axios
      .post("/api/v1/tour/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        console.log(response)
        if (response.data.status === "success") {
          setWaitlsit({
            lists: response.data.data.list,
            count: response.data.data.count
          })
        }
      })
      .catch(err => {
        alert(" 프라이빗 상품 리스트를  불러오는데 실패했습니다.")
      })
  }, [])
  const onSearchHandler = () => {
    const form = new FormData()
    form.append("index", 1)
    form.append("type", 1)
    form.append("search", seachtext)

    axios
      .post("/api/v1/tour/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        console.log(response)
        if (response.data.status === "success") {
          setWaitlsit({
            lists: response.data.data.list,
            count: response.data.data.count
          })
        }
      })
      .catch(err => {
        alert("검색한데이터를 불러오지못했습니다")
      })
  }
  const fn = async d => {
    let form = new FormData()
    form.append("index", d)
    form.append("type", 1)
    form.append("search", seachtext)

    try {
      const { data } = await axios.post("/api/v1/tour/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })

      if (data.status == "success") {
        setWaitlsit({
          lists: data.data.list,
          count: data.data.count
        })
      }
    } catch (error) {
      alert("페이지를 로딩하는데 실패했어요.")
    }
  }
  return (
    <div>
      <div className="card">
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="card-body">
              <div className="user-search-bar">
                <div className="search-input">
                  <div className="form-group">
                    <input
                      className="form-control"
                      type="text"
                      placeholder="가이드명으로 검색하세요"
                      onChange={e => setserchText(e.target.value)}
                      onKeyDown={e => {
                        if (e.key === "Enter") {
                          onSearchHandler()
                        }
                      }}
                    />
                  </div>
                </div>
                <div className="icon-search">
                  <i className="fa fa-search" />
                </div>
              </div>

              <div className="table-responsive">
                <table className="table">
                  <thead className="thead-light">
                    <tr>
                      <th scope="col" />
                      <th scope="col">승인상태</th>
                      <th scope="col">작성일시</th>
                      <th scope="col">타입</th>
                      <th scope="col">상품정보</th>
                      <th scope="col" />
                      <th scope="col" />
                      <th scope="col" />
                      <th scope="col" />
                    </tr>
                  </thead>
                  <tbody>
                    {waitlsit.lists &&
                      waitlsit.lists.map((lists, index) => {
                        return (
                          <tr>
                            <th scope="row" key={index}>
                              {index}
                            </th>
                            <td>{lists.status}</td>
                            <td>{lists.registered}</td>
                            <td> {lists.type}</td>
                            <td>
                              {" "}
                              <img
                                src={lists.rep_image}
                                alt="Gallery"
                                className="img-60 b-r-15"
                              />
                            </td>
                            <td>
                              {" "}
                              {lists.en_name}
                              <br />
                              {lists.zh_name}
                            </td>
                            <td>
                              <p>{lists.product}</p>
                              <p>{lists.product}</p>
                            </td>
                            <td>
                              <br />
                              CNY : {lists.zh_price} &nbsp;| &nbsp; USD:{" "}
                              {lists.en_price}
                            </td>

                            <td>
                              <button
                                className="  btn btn-secondary btn-sm"
                                onClick={() => {
                                  history.push(`/gorp/ditail/${lists.tour_no}`)
                                }}
                              >
                                보기
                              </button>
                              {/* <button
                              className="user-view-button"
                              onClick={() => onRemove(a.id)}
                            >
                              삭제
                            </button> */}
                            </td>
                          </tr>
                        )
                      })}
                  </tbody>
                </table>
                <div className="pagenation-box">
                  <Pagenation fn={fn} data={waitlsit} />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

function GuideOk() {
  const [seachtext, setserchText] = useState("")

  const [okLists, setOklists] = useState({})

  const history = useHistory()

  useEffect(() => {
    let form = new FormData()
    form.append("index", 1)
    form.append("type", 2)
    form.append("search", seachtext)
    axios
      .post("/api/v1/tour/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        console.log(response)
        if (response.data.status === "success") {
          setOklists({
            lists: response.data.data.list,
            count: response.data.data.count
          })
        }
      })
      .catch(err => {
        alert(" 프라이빗 상품 리스트를  불러오는데 실패했습니다.")
      })
  }, [])
  const onSearchHandler = () => {
    const form = new FormData()
    form.append("index", 1)
    form.append("type", 2)
    form.append("search", seachtext)

    axios
      .post("/api/v1/secret/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        console.log(response)
        if (response.data.status === "success") {
          setOklists(response.data.data.list)
        }
      })
      .catch(err => {
        alert("검색한데이터를 불러오지못했습니다")
      })
  }
  const fn = async d => {
    let form = new FormData()
    form.append("index", d)
    form.append("type", 2)

    try {
      const { data } = await axios.post("/api/v1/guide/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })

      if (data.status == "success") {
        setOklists({
          lists: data.data.list,
          count: data.data.count
        })
      }
    } catch (error) {
      alert("페이지를 로딩하는데 실패했어요.")
    }

    // 이전 다음 페이지를 위한 api호출
    // axios로 서버 통신
    // success시 nomalList 업데이트
  }
  return (
    <div>
      <div className="card">
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="card-body">
              <div className="user-search-bar">
                <div className="search-input">
                  <div className="form-group">
                    <input
                      className="form-control"
                      type="text"
                      placeholder="가이드명으로 검색하세요"
                      onChange={e => setserchText(e.target.value)}
                      onKeyDown={e => {
                        if (e.key === "Enter") {
                          onSearchHandler()
                        }
                      }}
                    />
                  </div>
                </div>
                <div className="icon-search">
                  <i className="fa fa-search" />
                </div>
              </div>

              <div className="table-responsive">
                <table className="table">
                  <thead className="thead-light">
                    <tr>
                      <th scope="col" />
                      <th scope="col">승인상태</th>
                      <th scope="col">작성일시</th>
                      <th scope="col">타입</th>
                      <th scope="col">상품정보</th>
                      <th scope="col" />
                      <th scope="col" />
                      <th scope="col" />
                      <th scope="col" />
                    </tr>
                  </thead>
                  <tbody>
                    {okLists.lists &&
                      okLists.lists.map((lists, index) => {
                        return (
                          <tr>
                            <th scope="row" key={index}>
                              {index}
                            </th>
                            <td>{lists.status}</td>
                            <td>{lists.registered}</td>
                            <td> {lists.type}</td>
                            <td>
                              {" "}
                              <img
                                src={lists.rep_image}
                                alt="Gallery"
                                className="img-60 b-r-15"
                              />
                            </td>
                            <td>
                              {" "}
                              {lists.en_name}
                              <br />
                              {lists.zh_name}
                            </td>
                            <td>
                              <p>{lists.product}</p>
                              <p>{lists.product}</p>
                            </td>
                            <td>
                              <br />
                              CNY : {lists.zh_price} &nbsp;| &nbsp; USD:{" "}
                              {lists.en_price}
                            </td>

                            <td>
                              <button
                                className="  btn btn-secondary btn-sm"
                                onClick={() => {
                                  history.push(`/gorp/ditail/${lists.tour_no}`)
                                }}
                              >
                                보기
                              </button>
                              {/* <button
                              className="user-view-button"
                              onClick={() => onRemove(a.id)}
                            >
                              삭제
                            </button> */}
                            </td>
                          </tr>
                        )
                      })}
                  </tbody>
                </table>
                <div className="pagenation-box">
                  <Pagenation data={okLists} fn={fn} />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
function TabContent2(props) {
  if (props.tab === 0) {
    return (
      <div>
        <GuideSome />
      </div>
    )
  } else if (props.tab === 1) {
    return (
      <div>
        <GuideOk />
      </div>
    )
  }
}
export default GorpGoodsList
