import React, { useContext, useState, useEffect } from "react"
import { useHistory, useParams } from "react-router-dom"
import Data from "../Data"
import { Modal, Button } from "react-bootstrap"
import GlobalContext from "../context/global.context"
import Breadcrumb from "../components/common/breadcrumb"
import SweetAlert from "react-bootstrap-sweetalert"
import Lightbox from "react-image-lightbox"
import axios from "axios"
import Cookie from "js-cookie"
import one from "../assets/images/product/1.png"

const images = [
  require("../assets/images/big-lightgallry/013.jpg"),
  require("../assets/images/big-lightgallry/014.jpg"),
  require("../assets/images/big-lightgallry/015.jpg"),
  require("../assets/images/big-lightgallry/016.jpg"),
  require("../assets/images/big-lightgallry/01.jpg"),
  require("../assets/images/big-lightgallry/04.jpg"),
  require("../assets/images/big-lightgallry/05.jpg"),
  require("../assets/images/big-lightgallry/06.jpg"),
  require("../assets/images/big-lightgallry/07.jpg"),
  require("../assets/images/big-lightgallry/08.jpg"),
  require("../assets/images/big-lightgallry/07.jpg"),
  require("../assets/images/big-lightgallry/08.jpg")
]

const smallImages = [
  require("../assets/images/lightgallry/013.jpg"),
  require("../assets/images/lightgallry/015.jpg"),
  require("../assets/images/lightgallry/014.jpg"),
  require("../assets/images/lightgallry/016.jpg"),
  require("../assets/images/lightgallry/01.jpg"),
  require("../assets/images/lightgallry/04.jpg"),
  require("../assets/images/lightgallry/05.jpg"),
  require("../assets/images/lightgallry/06.jpg"),
  require("../assets/images/lightgallry/07.jpg"),
  require("../assets/images/lightgallry/08.jpg"),
  require("../assets/images/lightgallry/07.jpg"),
  require("../assets/images/lightgallry/08.jpg")
]

const Userdetail = ({ match }) => {
  // const Datacontext = React.createContext();
  // const 데이터 = useContext(Datacontext);
  const userInfo = {
    register: "",
    nickName: "",
    lang: "",
    email: "",
    social: ""
  }

  const [userData, setUserData] = useState(userInfo)

  const [modal, setmodal] = useState(0)
  const [isOpen, setIsOpen] = useState(false)
  const [photoIndex, setPhotoIndex] = useState(0)
  const [Okbox, setOkbox] = useState(0)
  const [susees, setsusees] = useState(0)
  const [Outbox, setOutbox] = useState(0)
  const [Susees, setSusees] = useState(0)
  const [box, setbox] = useState(false)
  const ShowOkayMasege = () => {
    setSusees(1)
    setbox(false)
  }
  const HideOkMmsege = () => {
    setSusees(0)
  }
  const ShowAlert = () => {
    setbox(!box)
  }

  useEffect(() => {
    let form = new FormData()
    form.append("account_no", match.params.id)

    axios
      .post("/api/v1/member/detail", form, {
        headers: { Authorization: Cookie.get("token") }
      })
      .then(response => {
        if (response.data.status === "success") {
          setUserData({
            register: response.data.data.registered,
            nickName: response.data.data.nickname,
            lang: response.data.data.lang,
            email: response.data.data.email,
            social: response.data.data.social
          })
        }
      })
      .catch(err => {
        console.log(err)
        alert("유저정보 를 불러오는데 실패했습니다.")
      })
  }, [])

  // const showMasege = () => {
  //   setOkbox(1)
  // }
  // const noshowMasege = () => {
  //   setOkbox(0)
  // }
  // const okayMasege = () => {
  //   setsusees(1)
  // }

  const SizeUp = () => {
    setIsOpen(true)
    setPhotoIndex(1)
  }

  const Onhide = () => {
    setmodal(3)
  }

  const hidereview = () => {
    setmodal(0)
    setsusees(2)
  }
  const reviwhdcancle = () => {
    setmodal(4)
  }
  // const [alert, setalert] = useState(null)
  // const [show, setshow] = useState(false)
  // const [basicTitle, setbasicTitle] = useState("")
  // const [basicType, setbasicType] = useState("default")

  // const closeAlert = () => {
  //   setshow(null)
  // }
  // const hideAlert = () => {
  //   setalert(null)
  // }

  return (
    <div>
      <Breadcrumb
        title="유저관리"
        parent="유저관리
     "
      />
      <div className="card">
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            {/* <div onClick={() => onChangeGlobal({ testData: "얍" })}>btn</div> */}
            <div className="card-body">
              <div className="withing-product-detail__info">
                <div className="withing-product-detail__info-inner">
                  <div className="withing-product-detail__label">
                    {/* <strong className="c-title"> 가이드명 :{userData.style}</strong> */}
                  </div>
                  <div className="withing-product-detail__img">
                    <img src={userData.profile_image} alt="" />
                  </div>
                  <div className="withing-product-detail__title">
                    <h3 className="c-title">
                      <span className="text">닉네임: {userData.nickName}</span>
                      <span className="text">소셜:{userData.social}</span>
                    </h3>
                  </div>
                  <div className="withing-product-detail__price">
                    <ul className="withing-product-detail__price-list">
                      <li>
                        <span className="c-box" />
                      </li>
                    </ul>
                  </div>
                </div>
              </div>
              <div className="withing-product-detail__part">
                <ul className="withing-product-detail__part-list">
                  <li>
                    <strong className="c-title">자격증</strong>

                    <img
                      className="img-100 b-r-15"
                      src={userData.guide_image}
                    />
                  </li>

                  <li>
                    <strong className="c-title">사용 언어</strong>
                    <strong>
                      <div className="withing-product-detail__desc-text">
                        {userData.lang}
                      </div>
                    </strong>
                  </li>
                  <li>
                    <strong className="c-title">해시태그</strong>
                  </li>
                  {/* <li>
            <strong className="c-title">평점</strong>
            <span className="c-text">4.0</span>
          </li> */}
                </ul>
              </div>
              <div className="bennerContent">
                <div className="benner">
                  <h3>유저상세</h3>
                </div>
                <div className="benner">
                  <div className="benner-btn">
                    <button
                      className="btn btn-danger active  "
                      onClick={ShowAlert}
                    >
                      탈퇴
                    </button>
                  </div>
                </div>
              </div>

              <div className="card-block row">
                <div className="col-sm-12 col-lg-12 col-xl-12" id="bennerH">
                  <div className="test-wrapper">
                    <div className="left">
                      <div>가입일시</div>
                      <div>소셜</div>
                      <div>언어</div>
                      <div>이메일</div>
                      <div>닉네임</div>
                    </div>
                    <div className="right">
                      <div>{userData.register}</div>
                      <div>{userData.social}</div>
                      <div>{userData.lang}</div>
                      <div>{userData.email}</div>
                      <div>{userData.nickName}</div>
                    </div>
                  </div>
                  <div className="user-info-table">
                    <div className="card-header">
                      <h5>이용내역</h5>
                    </div>
                    <div className="table-responsive">
                      <table className="table">
                        <thead className="thead-light">
                          <tr>
                            <th scope="col">결제일자</th>
                            <th scope="col">이용일자</th>
                            <th scope="col">타입</th>
                            <th scope="col">상세정보</th>
                            <th scope="col">상품금액</th>
                            <th scope="col">결제금액</th>
                            <th scope="col">비고</th>
                          </tr>
                        </thead>
                        <tbody>
                          {/* {Data.map(a => {
                            return (
                              <tr>
                                <th scope="row" key={a.id}>
                                  {a.id}
                                </th>
                                <td>{a.datea}</td>
                                <td>{a.sns}</td>
                                <td>{a.lang}</td>
                                <td>{a.email}</td>
                                <td>{a.nicname}</td>
                                <td />
                              </tr>
                            )
                          })} */}

                          <tr>
                            <th />
                            <th />
                            <th />
                            <th />
                            <th />
                            <th />
                            <th />
                          </tr>
                        </tbody>
                      </table>
                    </div>
                    <div>
                      <div className="table-responsive">
                        <div className="sksk">
                          <table>
                            <thead>
                              <tr>
                                <td>날짜</td>
                                <td>후기내용들어갈자리</td>
                                <td />
                                <td />
                                <td />

                                <td>
                                  <img
                                    src={one}
                                    alt="Gallery"
                                    className="img-50 b-r-15"
                                    onClick={SizeUp}
                                  />
                                  &nbsp;
                                  <img
                                    src={one}
                                    alt="Gallery"
                                    className="img-50 b-r-15"
                                    onClick={SizeUp}
                                  />
                                  &nbsp;
                                  <img
                                    src={one}
                                    alt="Gallery"
                                    className="img-50 b-r-15"
                                    onClick={SizeUp}
                                  />
                                  &nbsp;
                                  <button
                                    className=" btn btn-danger btn-xs"
                                    onClick={Onhide}
                                  >
                                    숨기기
                                  </button>
                                </td>
                              </tr>
                            </thead>
                          </table>
                        </div>
                        {isOpen && (
                          <Lightbox
                            mainSrc={images[photoIndex]}
                            nextSrc={images[(photoIndex + 1) % images.length]}
                            prevSrc={
                              images[
                                (photoIndex + images.length - 1) % images.length
                              ]
                            }
                            imageTitle={photoIndex + 1 + "/" + images.length}
                            onCloseRequest={() => setIsOpen(false)}
                            onMovePrevRequest={() =>
                              setPhotoIndex({
                                photoIndex:
                                  (photoIndex + images.length - 1) %
                                  images.length
                              })
                            }
                            onMoveNextRequest={() =>
                              setPhotoIndex({
                                photoIndex: (photoIndex + 1) % one.length
                              })
                            }
                          />
                        )}
                      </div>
                    </div>
                  </div>
                </div>
                {box === true && (
                  <SweetAlert
                    danger
                    confirmBtnText="유저탈퇴 "
                    confirmBtnBsStyle="danger"
                    showCancel
                    title="유저를 탈퇴시키겠습니까?

                          "
                    onCancel={ShowAlert}
                    onConfirm={ShowOkayMasege}
                    focusCancelBtn
                  >
                    판매중지 처리시판매하고있는 상품데이터가 모두 사라집니다
                  </SweetAlert>
                )}

                {Susees === 1 && (
                  <SweetAlert
                    success
                    title="유저탈퇴 처리가완료되었습니다"
                    onConfirm={HideOkMmsege}
                  />
                )}

                {modal === 3 && (
                  <SweetAlert
                    danger
                    confirmBtnText="리뷰숨기기 "
                    confirmBtnBsStyle="danger"
                    showCancel
                    title="해당유저의 후기를 숨기시겠습니까?"
                    onCancel={reviwhdcancle}
                    onConfirm={hidereview}
                    focusCancelBtn
                  >
                    판매중지 처리시판매하고있는 상품데이터가 모두 사라집니다
                  </SweetAlert>
                )}
                {Susees === 2 && (
                  <SweetAlert
                    success
                    title="후기 숨김처림이완료되었습니다"
                    onConfirm={HideOkMmsege}
                  />
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

const OkModal = porps => {
  return (
    <div>
      <Modal.Dialog>
        <Modal.Header closeButton>
          <Modal.Title>탈퇴여부</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <p>탈퇴처리시 유저데이터가 모두사라집니다 탈퇴처리되었습니다</p>
        </Modal.Body>

        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => {
              porps.setmodal(0)
            }}
          >
            확인
          </Button>
        </Modal.Footer>
      </Modal.Dialog>
    </div>
  )
}

const HideModal = props => {
  return (
    <div>
      <Modal.Dialog>
        <Modal.Header closeButton>
          <Modal.Title>탈퇴여부</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <p>해당 후기를 숨기시면 앱에서 노출되지않습니다 숨기시겠습니까?</p>
        </Modal.Body>

        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => {
              props.setmodal(0)
            }}
          >
            확인
          </Button>
        </Modal.Footer>
      </Modal.Dialog>
    </div>
  )
}
export default Userdetail
