import React, { useState, useEffect } from "react"
import { useHistory } from "react-router-dom"
import "../assets/css/layout.css"
import { Nav } from "react-bootstrap"
import Data from "../Data"
import Breadcrumb from "../components/common/breadcrumb"
import PagnationTool from "../PagnationTool"
import one from "../assets/images/product/1.png"
import axios from "axios"
import Cookie from "js-cookie"
function Guidelist() {
  const history = useHistory()
  let [tab, settab] = useState(0)

  return (
    <>
      <Breadcrumb title="가이드 관리" parent="가이드 관리" />
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm-12">
            <div className="card">
              <div className="card-header">
                <div className="card-body">
                  <div className="nav-sub">
                    <Nav
                      variant="tabs"
                      defaultActiveKey="link-0"
                      className="nav--sub2"
                    >
                      <div className="navtab">
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-0"
                            onClick={() => {
                              settab(0)
                            }}
                          >
                            승인 상태
                          </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-1"
                            onClick={() => {
                              settab(1)
                            }}
                          >
                            활동중
                          </Nav.Link>
                        </Nav.Item>
                      </div>
                      <div>
                        {/* <button
                          id="maching-btn"
                          className="btn btn-danger btn-sm"
                          onClick={() => {
                            history.push('/recomendguidecnt');
                          }}
                        >
                          상품연결
                        </button> */}
                      </div>
                    </Nav>
                    <TabContent2 tab={tab} />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* <div className="card-block row">
        <div className="col-sm-12 col-lg-12 col-xl-12"></div>
      </div> */}
    </>
  )
}
function TabContent2(props) {
  if (props.tab === 0) {
    return (
      <div>
        <GuideSome />
      </div>
    )
  } else if (props.tab === 1) {
    return (
      <div>
        <GuideOk />
      </div>
    )
  }
}

function GuideSome() {
  const history = useHistory()
  const [waitlsit, setWaitlsit] = useState([])
  const [searchText, setSearchText] = useState()

  useEffect(() => {
    let form = new FormData()
    form.append("index", 1)
    form.append("type", 1)
    axios
      .post("/api/v1/guide/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        if (response.data.status === "success") {
          setWaitlsit(response.data.data.list)
        }
      })
      .catch(err => {
        alert("가이드리스트를  불러오는데 실패했습니다.")
      })
  }, [])
  const onSearchHandler = () => {
    const form = new FormData()
    form.append("index", 1)
    form.append("type", 1)
    form.append("search", searchText)

    axios
      .post("/api/v1/guide/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        if (response.data.status === "success") {
          setWaitlsit(response.data.data.list)
        }
      })
      .catch(err => {
        alert("검색한데이터를 불러오지못했습니다")
      })
  }

  return (
    <div>
      <div className="card">
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="card-body">
              <div className="user-search-bar">
                <div className="search-input">
                  <div className="form-group">
                    <input
                      className="form-control"
                      type="text"
                      placeholder="가이드명으로 검색하세요"
                      onChange={e => setSearchText(e.target.value)}
                      onKeyDown={e => {
                        if (e.key === "Enter") {
                          onSearchHandler()
                        }
                      }}
                    />
                  </div>
                </div>
                <div onClick={""} className="icon-search">
                  <i className="fa fa-search" />
                </div>
              </div>

              <div className="table-responsive">
                <table className="table">
                  <thead className="thead-light">
                    <tr>
                      <th scope="col" />
                      <th scope="col">승인상태</th>
                      <th scope="col">작성일시</th>
                      <th scope="col">가이드정보</th>
                      <th scope="col" />
                      <th scope="col" />
                    </tr>
                  </thead>
                  <tbody>
                    {waitlsit.map((list, index) => {
                      return (
                        <tr>
                          <th scope="row" key={list.guide_no} />
                          <td>{list.status}</td>
                          <td>{list.registered}</td>

                          <td>
                            {" "}
                            <img
                              src={one}
                              alt="Gallery"
                              className="img-60 b-r-15"
                            />
                          </td>
                          <td>
                            가이드명 : {list.name}
                            {/* /{lists.age}/{lists.lang}/{lists.area} */}
                          </td>

                          <td>
                            <button
                              className="  btn btn-secondary btn-sm"
                              onClick={() => {
                                history.push(`/guideditail/${list.guide_no}`)
                              }}
                            >
                              보기
                            </button>
                            {/* <button
                              className="user-view-button"
                              onClick={() => onRemove(a.id)}
                            >
                              삭제
                            </button> */}
                          </td>
                        </tr>
                      )
                    })}
                  </tbody>
                </table>
                <PagnationTool />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
function GuideOk(props) {
  const history = useHistory()
  const [waitlsit, setWaitlsit] = useState([])
  const [searchText, setSearchText] = useState()
  const [per, setPer] = useState(1)
  const [arr, setArr] = useState([])
  // 실제 페이지네이션을 쪼개서 뿌려주는 리스트
  const [list, setList] = useState([])

  const [page, setPage] = useState({
    start: 0,
    end: 10,
    current: 1
  })

  const pageBlock = Math.ceil(waitlsit.length / per)
  const array = []
  for (let i = 0; i < pageBlock; i++) {
    array.push(i + 1)
  }
  useEffect(() => {
    const start = ((page.current - 1) * 10) / 2
    const end = Math.floor(start + 9 / 2)

    const list = waitlsit.slice(start, end + 1)
    setList(list)
    setArr(array.slice(page.start, page.end))
  }, [page.current])

  const onPageUp = () => {
    if (page.current < arr[arr.length - 1]) {
      setPage({ ...page, current: page.current + 1 })
    } else if (page.current % page.end === 0 && page.end < pageBlock) {
      setPage({
        start: page.start + 1,
        end: page.end + 1,
        current: page.current + 1
      })
      setArr(array.slice(page.start + 1, page.end + 1))
    }
  }

  const tenPageUP = () => {
    const lastIndex = array.length

    if (lastIndex <= 10 && page.current <= 10) {
      setPage({ ...page, current: lastIndex })
    } else {
      const maxIndex = array[page.current + 9]
      if (maxIndex) {
        setPage({ ...page, current: maxIndex })
      } else {
        setPage({ ...page, current: lastIndex })
      }
    }
    // if (page.current < arr)
  }
  const tenPageDown = () => {
    const minusCheck = page.current - 10
    if (Math.sign(minusCheck) <= 0) {
      setPage({ ...page, current: 1 })
    } else {
      setPage({ ...page, current: minusCheck })
    }
  }

  const onPageDown = () => {
    if (page.current > arr[0]) {
      setPage({ ...page, current: page.current - 1 })
    } else if (page.current % arr[0] === 0 && page.start >= 1) {
      setPage({
        start: page.start - 1,
        end: page.end - 1,
        current: page.current - 1
      })
      setArr(array.slice(page.start - 1, page.end - 1))
    }
  }
  useEffect(() => {
    let form = new FormData()
    form.append("index", 1)
    form.append("type", 2)
    axios
      .post("/api/v1/guide/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        console.log(response)
        if (response.data.status === "success") {
          setWaitlsit(response.data.data.list)
        }
      })
      .catch(err => {
        alert("가이드리스트를  불러오는데 실패했습니다.")
      })
  }, [])
  const onSearchHandler = () => {
    const form = new FormData()
    form.append("index", 1)
    form.append("type", 2)
    form.append("search", searchText)

    axios
      .post("/api/v1/guide/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        console.log(response)
        if (response.data.status === "success") {
          if (response.data.data.count > 0) {
            setWaitlsit(response.data.data.list)
          } else {
            alert("일치하는 가이드가 없습니다")
            setWaitlsit([])
          }
          console.log(waitlsit)
        }
      })
      .catch(err => {
        alert("검색한데이터를 불러오지못했습니다")
      })
  }

  return (
    <div>
      <div className="card">
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="card-body">
              <div className="user-search-bar">
                <div className="search-input">
                  <div className="form-group">
                    <input
                      className="form-control"
                      type="text"
                      placeholder="가이드명으로 검색하세요"
                      onChange={e => setSearchText(e.target.value)}
                      onKeyDown={e => {
                        if (e.key === "Enter") {
                          onSearchHandler()
                        }
                      }}
                    />
                  </div>
                </div>
                <div onClick={""} className="icon-search">
                  <i className="fa fa-search" />
                </div>
              </div>

              <div className="table-responsive">
                <table className="table">
                  <thead className="thead-light">
                    <tr>
                      <th scope="col" />
                      <th scope="col">승인상태</th>
                      <th scope="col">작성일시</th>
                      <th scope="col">가이드정보</th>
                      <th scope="col" />
                      <th scope="col" />
                    </tr>
                  </thead>
                  <tbody>
                    {waitlsit.map((list, index) => {
                      return (
                        <tr>
                          <th scope="row" key={list.guide_no} />
                          <td>{list.status}</td>
                          <td>{list.registered}</td>
                          {/* API에 이미지가없음 */}
                          <td>
                            {" "}
                            <img
                              src={one}
                              alt="Gallery"
                              className="img-60 b-r-15"
                            />
                          </td>
                          <td>
                            {list.name}
                            {/* /{lists.age}/{lists.lang}/{lists.area} */}
                          </td>

                          <td>
                            <button
                              className="  btn btn-secondary btn-sm"
                              onClick={() => {
                                history.push(`/guideditail/ok/${list.guide_no}`)
                              }}
                            >
                              보기
                            </button>
                            {/* <button
                              className="user-view-button"
                              onClick={() => onRemove(a.id)}
                            >
                              삭제
                            </button> */}
                          </td>
                        </tr>
                      )
                    })}
                  </tbody>
                </table>
                <div className="withing-pagenation">
                  {/* <!-- [D] only for pc : 10페이지 단위로 이동, 처음 페이지 일 경우 및 미노출시 is-disabled 클래스명 추가 및 disabled 추가 --> */}
                  <button
                    type="button"
                    className="withing-pagenation__cta btn-prev2"
                    onClick={tenPageDown}
                  >
                    <span className="blind">10페이지 전으로 이동</span>
                  </button>
                  <button
                    type="button"
                    className="withing-pagenation__cta btn-prev"
                    onClick={onPageDown}
                  >
                    <span className="blind">이전 페이지로 이동</span>
                  </button>
                  <div className="withing-pagenation__link">
                    {/* <!-- [D] 선택된 경우 is-active 클래스명 추가 --> */}
                    {array.map(a => (
                      <a
                        key={a}
                        // href="#"
                        className={`page-link ${
                          page.current === a ? "is-active" : ""
                        }`}
                        onClick={() => setPage({ ...page, current: a })}
                      >
                        <span class="num">{a}</span>
                      </a>
                    ))}
                  </div>
                  <button
                    type="button"
                    className="withing-pagenation__cta btn-next"
                    onClick={onPageUp}
                  >
                    <span className="blind">다음 페이지로 이동</span>
                  </button>
                  {/* /* <!— [D] only for pc : 10페이지 단위로 이동, 마지막 페이지 일 경우 및 미노출시 is-disabled 클래스명 추가 —> */}
                  <button
                    type="button"
                    className="withing-pagenation__cta btn-next2"
                    onClick={tenPageUP}
                  >
                    <span className="blind">10페이지 다음으로 이동</span>
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
export default Guidelist
