import React, { useContext, useState, useEffect } from "react"
import { useHistory } from "react-router-dom"
import "../assets/css/layout.css"
import NewCard from "../components/common/NewCard"
import PagnationTool from "../PagnationTool"
import Lightbox from "react-image-lightbox"
import "react-image-lightbox/style.css"
import axios from "axios"
import Cookie from "js-cookie"
import { message } from "antd"
import Pagenation from "../util/Pagenation"
const KoreaNewsUserList = () => {
  const history = useHistory()
  const [isOpen, setIsOpen] = useState(false)
  const [photoIndex, setPhotoIndex] = useState(0)
  const [newsList, setNewsList] = useState({})
  useEffect(() => {
    let form = new FormData()
    form.append("index", 1)

    axios
      .post("/api/v1/news/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        console.log(response)
        if (response.data.status === "success") {
          setNewsList({
            lists: response.data.data.list,
            count: response.data.data.count
          })
        }
      })
      .catch(err => {
        alert("한국소식을  불러오는데 실패했습니다.")
      })
  }, [])

  const fn = async d => {
    let form = new FormData()
    form.append("index", d)

    try {
      const { data } = await axios.post("/api/v1/news/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })

      if (data.status == "success") {
        setNewsList({
          lists: data.data.list,
          count: data.data.count
        })
      }
    } catch (error) {
      message.warning("페이지를 로딩하는데 실패했어요.")
    }

    // 이전 다음 페이지를 위한 api호출
    // axios로 서버 통신
    // success시 nomalList 업데이트
  }
  return (
    <>
      <NewCard title="한국소식리스트" parent="한국소식관리">
        <div className="bennerContent">
          <h5>한국소식리스트</h5>
          <button
            className="btn btn-secondary "
            onClick={() => {
              history.push("/koreanewsadd")
            }}
          >
            한국소식등록
          </button>
        </div>
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="table-responsive">
              <table className="table">
                <thead className="thead-light">
                  <tr>
                    <th scope="col" style={{ width: "5%" }} />
                    <th scope="col" style={{ width: "10%" }}>
                      작성일시
                    </th>
                    <th scope="col" style={{ width: "5%" }}>
                      카테고리
                    </th>
                    <th scope="col" style={{ width: "25%" }}>
                      대표이미지
                    </th>
                    <th scope="col" style={{ width: "35%" }}>
                      제목
                    </th>
                    <th scope="col" style={{ width: "20%" }} />
                  </tr>
                </thead>
                <tbody>
                  {newsList.lists &&
                    newsList.lists.map((lists, index) => {
                      return (
                        <tr>
                          <th scope="row" key={index}>
                            {index}
                          </th>
                          <td>{""}</td>
                          <td>{lists.registered}</td>
                          <td>
                            <img
                              src={lists.zh_image}
                              alt="Gallery"
                              className="img-100 b-r-15"
                              onClick={() => {
                                setIsOpen(true)
                                setPhotoIndex(1)
                              }}
                            />
                          </td>
                          <td>
                            <strong>{lists.zh_title}</strong>
                            <br />
                            <strong>{lists.en_title}</strong>
                          </td>
                          <td>
                            <button
                              className="btn btn-warning active  btn -xs btn-sm"
                              type="button"
                            >
                              삭제
                            </button>
                            &nbsp; &nbsp;
                            {/* <button className="edit-button">수정</button>{' '} */}
                            <button
                              className="btn btn-secondary btn-sm"
                              type="button"
                            >
                              수정
                            </button>
                          </td>
                        </tr>
                      )
                    })}
                </tbody>
              </table>
            </div>
          </div>
        </div>
        <div className="pagenation-box">
          <Pagenation data={newsList} fn={fn} />
        </div>
      </NewCard>
    </>
  )
}

export default KoreaNewsUserList
