import React, { useContext } from 'react';
import Data from '../Data';
import '../assets/css/layout.css';
import { Search } from 'react-feather';
import { useHistory, useParams } from 'react-router-dom';
const UserList = () => {
  const [seachtext, setserchText] = React.useState('');
  const [dummyData, setDummyData] = React.useState(Data);
  const SerchFilter = () => {
    const filter = Data.filter((d) => d.nicname.indexOf(seachtext) > -1);
    setDummyData(filter);
  };
  const onRemove = (id) => {
    setDummyData(dummyData.filter((user) => user.id !== id));
  };
  const history = useHistory();
  const { id } = useParams();

  return (
    <div>
      <div className="card">
        <div className="card-header">
          <h5>유저관리</h5>
        </div>
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="user-search">
              <form className="form-inline search-form">
                <input
                  className="form-control-plaintext searchIcon"
                  type="text"
                  placeholder="닉네임으로 검색하세요"
                  onChange={(e) => setserchText(e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                      SerchFilter();
                    }
                  }}
                />
                <span className="d-sm-none mobile-search">
                  <Search />
                </span>
              </form>
            </div>
            <div className="table-responsive">
              <table className="table">
                <thead className="thead-light">
                  <tr>
                    <th scope="col"></th>
                    <th scope="col">가입일시</th>
                    <th scope="col">소셜구분</th>
                    <th scope="col">언어</th>
                    <th scope="col">이메일</th>
                    <th scope="col">닉네임</th>
                    <th scope="col"></th>
                  </tr>
                </thead>
                <tbody>
                  {dummyData.map((a) => {
                    return (
                      <tr>
                        <th scope="row" key={a.id}>
                          {a.id}
                        </th>
                        <td>{a.datea}</td>
                        <td>{a.sns}</td>
                        <td>{a.lang}</td>
                        <td>{a.email}</td>
                        <td>{a.nicname}</td>

                        <td>
                          <button
                            className="user-view-button"
                            onClick={() => {
                              history.push(`/userdetails/${a.id}`);
                            }}
                          >
                            보기
                          </button>
                          <button
                            className="user-view-button"
                            onClick={() => onRemove(a.id)}
                          >
                            삭제
                          </button>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default React.memo(UserList);
