import React, { useContext, useState, useEffect } from "react"

import "../assets/css/layout.css"
import axios from "axios"
import Cookie from "js-cookie"
import { useHistory, useParams } from "react-router-dom"
import Breadcrumb from "../components/common/breadcrumb"
import PagnationTool from "../PagnationTool"
import Pagenation from "../util/Pagenation"
const UserList = () => {
  const [searchText, setSerchText] = useState("")
  const [userlist, setUserlist] = useState({})

  useEffect(() => {
    let form = new FormData()
    form.append("index", 1)

    axios
      .post("/api/v1/member/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        if (response.data.status === "success") {
          setUserlist({
            lists: response.data.data.list,
            count: response.data.data.count
          })
          console.log(response.data.data.list)
        }
      })
      .catch(err => {
        alert("불러오기실패")
        console.log(err)
      })
  }, [])

  const onSearchHandler = () => {
    const form = new FormData()
    form.append("index", 1)
    form.append("search", searchText)

    axios
      .post("/api/v1/member/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        console.log(response.data.data.list)
        if (response.data.status === "success") {
          setUserlist({
            lists: response.data.data.list,
            count: response.data.data.count
          })
        }
      })
      .catch(err => {
        alert("검색한데이터를 불러오지못했습니다")
      })
  }

  // const onRemove = id => {
  //   setDummyData(user.filter(user => user.id !== id))
  // }
  const history = useHistory()

  const fn = async d => {
    let form = new FormData()
    form.append("index", d)

    try {
      const { data } = await axios.post("/api/v1/guide/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })

      if (data.status == "success") {
        setUserlist({
          lists: data.data.list,
          count: data.data.count
        })
      }
    } catch (error) {
      alert("페이지를 로딩하는데 실패했어요.")
    }

    // 이전 다음 페이지를 위한 api호출
    // axios로 서버 통신
    // success시 nomalList 업데이트
  }

  return (
    <div>
      <Breadcrumb title="유저관리" parent="유저관리" />
      <div className="card">
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="card-body">
              <div className="user-search-bar">
                <div className="search-input">
                  <div className="form-group">
                    <input
                      className="form-control"
                      type="text"
                      placeholder="닉네임으로 검색하세요"
                      onChange={e => setSerchText(e.target.value)}
                      onKeyDown={e => {
                        if (e.key === "Enter") {
                          onSearchHandler()
                        }
                      }}
                    />
                  </div>
                </div>
                <div onClick={""} className="icon-search">
                  <i className="fa fa-search" />
                </div>
              </div>

              <div className="table-responsive">
                <table className="table">
                  <thead className="thead-light">
                    <tr>
                      <th scope="col" />
                      <th scope="col">가입일시</th>

                      <th scope="col">언어</th>
                      <th scope="col">이메일</th>
                      <th scope="col">닉네임</th>
                      <th scope="col" />
                    </tr>
                  </thead>
                  <tbody>
                    {console.log(userlist.lists)}
                    {userlist.lists &&
                      userlist.lists.map((list, index) => {
                        return (
                          <tr>
                            <th scope="row" key={index}>
                              {list.account_no}
                            </th>
                            <td>{list.registered}</td>
                            <td>{list.lang}</td>
                            <td>{list.email}</td>
                            <td>{list.nickname}</td>

                            <td>
                              <button
                                className="  btn btn-secondary btn-sm"
                                onClick={() => {
                                  history.push(
                                    `/userdetails/${list.account_no}`
                                  )
                                }}
                              >
                                보기
                              </button>
                              {/* <button
                              className="user-view-button"
                              onClick={() => onRemove(a.id)}
                            >
                              삭제
                            </button> */}
                            </td>
                          </tr>
                        )
                      })}
                  </tbody>
                </table>
                <div className="pagenation-box">
                  <Pagenation data={userlist} fn={fn} />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default React.memo(UserList)
