import React, { useState, useRef } from "react"
import "../assets/css/layout.css"
import { useHistory, useParams } from "react-router-dom"

import DropZn from "./DropZn"
import Breadcrumb from "../components/common/breadcrumb"
import axios from "axios"
import CKEditors from "react-ckeditor-component"
import Cookie from "js-cookie"
import "react-dropzone-uploader/dist/styles.css"
import Dropzone from "react-dropzone-uploader"
import "codemirror/lib/codemirror.css"
import "@toast-ui/editor/dist/toastui-editor.css"
import { Editor } from "@toast-ui/react-editor"

const AddBenner = () => {
  const [Zheditor, setZheditor] = useState("")
  const [Eneditor, setEeditor] = useState("")
  const [Zhimage, setZhimage] = useState("")
  const [Enimage, setEnimage] = useState("")

  const history = useHistory()
  const enEediterContens = useRef()
  const zhEditerContens = useRef()
  const [EnBanner, setEnBanner] = useState([])

  const onZhContentHandler = (e) => {
    const content = e.editor.getData()

    setZheditor(content)
  }
  const onEnContentHandler = (e) => {
    const content = e.editor.getData()

    setEeditor(content)
  }
  const ZhText = () => {
    return (
      <div className="col-sm-12">
        <div className="card">
          <div className="card-header">
            <h5>중문내용입력하기</h5>
          </div>
          <div className="card-body">
            {/* <CKEditors
              editor={CKEditors}
              content={Zheditor}
              events={{
                change: onZhContentHandler,
              }}
            /> */}
            <Editor
              initialValue="니하오!"
              previewStyle="vertical"
              height="400px"
              ref={zhEditerContens}
              initialEditType="wysiwyg"
              // useCommandShortcut={true}
            />
          </div>
        </div>
      </div>
    )
  }

  const DropEn = () => {
    // specify upload params and url for your files
    const getUploadParams = ({ meta }) => {
      return { url: "https://httpbin.org/post" }
    }
    // called every time a file's `status` changes

    const handleChangeStatus = (files, status) => {
      // 파일정보
      if (status === "done") {
        setEnBanner([...EnBanner, { ...files.meta }])
      }
    }

    // receives array of files that are done uploading when submit button is clicked
    const handleSubmit = (files, allFiles) => {
      console.log(allFiles)
      allFiles.forEach((f) => f.remove())
      // toast.success("이미지를 업로드중입니다..!")
    }

    return (
      <div>
        <div className="col-sm-12">
          <div className="card">
            <div className="card-header">
              <h5>영문배너이미지</h5>
            </div>
            <div className="card-body">
              <form
                className="dropzone dropzone-info"
                id="fileTypeValidation"
                action="/upload.php"
              >
                <div className="dz-message needsclick">
                  <Dropzone
                    getUploadParams={getUploadParams}
                    onChangeStatus={handleChangeStatus}
                    maxFiles={5}
                    // onSubmit={handleSubmit}
                    accept="image/*,audio/*,video/*"
                  />
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    )
  }

  const DropZh = () => {
    // specify upload params and url for your files
    const getUploadParams = ({ meta }) => {
      return { url: "https://httpbin.org/post" }
    }
    // called every time a file's `status` changes

    const handleChangeStatus = ({ meta, file }, status) => {
      // 파일정보
    }

    // receives array of files that are done uploading when submit button is clicked
    const handleSubmit = (files, allFiles) => {
      console.log(allFiles)
      allFiles.forEach((f) => f.remove())
      // toast.success("이미지를 업로드중입니다..!")
    }

    return (
      <div>
        <div className="col-sm-12">
          <div className="card">
            <div className="card-header">
              <h5>중문배너이미지</h5>
            </div>
            <div className="card-body">
              <form
                className="dropzone dropzone-info"
                id="fileTypeValidation"
                action="/upload.php"
              >
                <div className="dz-message needsclick">
                  <Dropzone
                    getUploadParams={getUploadParams}
                    onChangeStatus={handleChangeStatus}
                    maxFiles={5}
                    // onSubmit={handleSubmit}
                    accept="image/*,audio/*,video/*"
                  />
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    )
  }

  const onSubmithandler = async (e) => {
    e.preventDefault()
    zhEditerContens.current.getInstance().getHtml()
    let form = new FormData()
    form.append("eh_editor", Zheditor)
    form.append("en_editor", Eneditor)
    form.append("zh_image", Eneditor)
    form.append("en_image", Eneditor)

    const cookie = Cookie.get("token")

    try {
      const { data } = await axios.post("/api/v1/banner/list", form, {
        headers: {
          Authorization: cookie,
        },
      })
      if (data.status == "success") {
        alert("성공적으로 작성되었습니다.")
        history.push("/bennerlist")
      }
    } catch (error) {
      alert("작성하는데 실패했습니다.")
    }
  }
  const EnText = () => {
    // console.log("????")
    return (
      <div className="col-sm-12">
        <div className="card">
          <div className="card-header">
            <h5>영문내용입력하기</h5>
          </div>
          <div className="card-body">
            <Editor
              initialValue="hello react editor world!"
              previewStyle="vertical"
              height="400px"
              ref={enEediterContens}
              initialEditType="wysiwyg"
              // useCommandShortcut={true}
            />
          </div>
        </div>
      </div>
    )
  }

  return (
    <>
      {console.log(EnBanner)}
      <Breadcrumb title="배너관리" parent="배너추가" />
      <div className="card">
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="card-body">
              <form>
                {/* <div> */}

                <div>{DropEn()}</div>
                <div>{DropZh()}</div>
                <div>{EnText()}</div>
                <div>{ZhText()}</div>
                <div style={{ marginLeft: "40px" }} className="btn-box-bner">
                  <button
                    className="btn btn-square btn-warning btn-sm"
                    type="button"
                  >
                    삭제
                  </button>
                  &nbsp; &nbsp;
                  {/* <button className="edit-button">수정</button>{' '} */}
                  <button
                    className="btn btn-square btn-secondary active btn-sm"
                    type="button"
                    onClick={onSubmithandler}
                  >
                    작성
                  </button>
                </div>

                {/* </div> */}
              </form>
            </div>
            {/* <div className="col-sm-12">
              <div> */}
            {/* </div>
            </div> */}
            {/* <div className="col-sm-12"> */}
            {/* </div>
            <div className="col-sm-12">
              <div> */}
          </div>
        </div>
      </div>
      {/* </div>
      </div> */}
    </>
  )
}

export default AddBenner
