import React, { useState, useEffect } from "react"
import "../assets/css/layout.css"

import Breadcrumb from "../components/common/breadcrumb"
import { EditorState, convertToRaw, ContentState } from "draft-js"
import htmlToDraftjs from "html-to-draftjs"
import axios from "axios"
import { Editor } from "react-draft-wysiwyg"

import Cookie from "js-cookie"
import DraftEditer from "../draftEditer/DraftEditer"
const NotiUserUpDate = ({ history, match }) => {
  const [ZhTitle, setZhTitle] = useState("")
  const [EnTitle, setEnTitle] = useState("")
  const [Zheditor, setZheditor] = useState(EditorState.createEmpty())
  const [Eneditor, setEneditor] = useState(EditorState.createEmpty())

  const convertHtml = data => {
    const toHtml = htmlToDraftjs(data)
    const { contentBlocks, entityMap } = toHtml
    const contentState = ContentState.createFromBlockArray(
      contentBlocks,
      entityMap
    )

    return EditorState.createWithContent(contentState)
  }

  useEffect(() => {
    let form = new FormData()

    form.append("notice_no", match.params.id)

    // 데이터 가져오기
    axios
      .post("/api/v1/notice/editpageU", form, {
        headers: { Authorization: Cookie.get("token") }
      })
      .then(function(response) {
        console.log(response)
        // 데이터불러오는데 성공했는지 확인 if
        setZhTitle(response.data.data.zh_title)
        setEnTitle(response.data.data.en_title)

        setZheditor(convertHtml(response.data.data.zh_editor))
        // setEneditor(convertHtml(response.data.data.en_editor))
      })
    // 데이터 가져온후에 state 바인딩
  }, [])
  const onEditerContentHandler = (editerContent, value) => {
    // 에디터에있는값을  넘겨줌
    if (value) {
      setZheditor(editerContent)
    } else {
      setEneditor(editerContent)
    }
  }

  const onSubmithandler = async e => {
    e.preventDefault()

    let form = new FormData()
    form.append("notice_no", match.params.id)
    form.append("zh_editor", Zheditor)
    form.append("en_editor", Eneditor)
    form.append("zh_title", ZhTitle)
    form.append("en_title", EnTitle)
    const cookie = Cookie.get("token")
    try {
      const { data } = await axios.post("/api/v1/notice/editU", form, {
        headers: {
          Authorization: cookie
        }
      })
      if (data.status === "success") {
        alert("성공적으로 수정되었습니다.")
        history.push("/notice")
      }
    } catch (error) {
      alert("작성하는데 실패했습니다.")
    }
  }

  const onUpload = files => {
    const form = new FormData()

    form.append("type", "A")
    form.append("file", files)

    return axios
      .post("/api/v1/file/uploadFile", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(res => {
        return { data: { link: res.data.data } }
      })
      .catch(err => {
        console.log(err)
      })
  }

  const onEditorStateChange = data => {
    setEneditor(data)
  }

  const onEditorZhChange = data => {
    setZheditor(data)
  }
  //   axios.post("/api/v1/notice/addU", form, {
  //     headers: {
  //       Authorization: cookie,
  //     },
  //   })
  // }
  // const { zh_title, en_title, zn_content, en_content } = inputs

  // const onEnContentHandler = e => {
  //   const content = e.editor.getData()

  //   setEneditor(content)
  // }

  // const onZhContentHandler = e => {
  //   const content = e.editor.getData()

  //   setZheditor(content)
  // }
  // let form = new FormData()
  // form.append("zh_title", zh_titleState)
  // form.append("en_title,", en_titleState)
  // axios
  //   .post("http://192.168.0.52:49156/api/v1/notice/addU", from)
  //   .then((response) => {
  //     console.log("response : ", JSON.stringify(response, null, 2))
  //   })
  //   .catch((error) => {
  //     console.log("failed", error)
  //   })

  // const EnText = () => (

  // )

  return (
    <>
      <Breadcrumb title="유저용공지사항 작성" parent="공지사항관리" />
      <div className="card">
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="card-body">
              <div className="noti-title">
                <form>
                  <h5>제목입력</h5>
                  <input
                    onChange={e => setEnTitle(e.target.value)}
                    value={EnTitle}
                    class="form-control"
                    id="exampleFormControlInput1"
                    type="input"
                    placeholder="영문으로입력하세요"
                  />
                  <p />
                  <input
                    onChange={e => setZhTitle(e.target.value)}
                    value={ZhTitle}
                    class="form-control"
                    id="exampleFormControlInput1"
                    type="input"
                    placeholder="중문으로입력하세요"
                  />
                </form>
              </div>
            </div>

            <div className="col-sm-12">
              <h5>영문내용</h5>
              <Editor
                editorState={Eneditor}
                toolbarClassName="toolbarClassName"
                wrapperClassName="home-wrapper"
                editorClassName="home-editor"
                onEditorStateChange={onEditorStateChange}
                toolbar={{
                  // inline: { inDropdown: true },
                  // list: { inDropdown: true },
                  // textAlign: { inDropdown: true },
                  // link: { inDropdown: true },
                  // history: { inDropdown: true },
                  image: { uploadCallback: onUpload }
                }}
                localization={{
                  locale: "ko"
                }}
              />
            </div>
            <h5>중문내용</h5>
            <div className="col-sm-12">
              <Editor
                editorState={Zheditor}
                toolbarClassName="toolbarClassName"
                wrapperClassName="home-wrapper"
                editorClassName="home-editor"
                onEditorStateChange={onEditorZhChange}
                toolbar={{
                  // inline: { inDropdown: true },
                  // list: { inDropdown: true },
                  // textAlign: { inDropdown: true },
                  // link: { inDropdown: true },
                  // history: { inDropdown: true },
                  image: { uploadCallback: onUpload }
                }}
                localization={{
                  locale: "ko"
                }}
              />
              {/* <DraftEditer onEditerContentHandler={onEditerContentHandler} /> */}
              <div>
                {/* <CkEditerZn onChange={onEnContentHandler} /> */}
                {}
                <div>
                  <button
                    className="btn btn-square btn-warning btn-sm"
                    type="button"
                  >
                    삭제
                  </button>
                  &nbsp; &nbsp;
                  {/* <button className="edit-button">수정</button>{' '} */}
                  <button
                    className="btn btn-square btn-secondary active btn-sm"
                    onClick={onSubmithandler}
                  >
                    작성
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default NotiUserUpDate
