import React, { useState, useEffect } from "react"
import { useHistory } from "react-router-dom"
import "../assets/css/layout.css"
import { Nav } from "react-bootstrap"
import Breadcrumb from "../components/common/breadcrumb"
import PagnationTool from "../PagnationTool"
import one from "../assets/images/user/1.jpg"
import axios from " axios"
import Cookie from "js-cookie"
const dummyData = [
  {
    id: "1",
    facility: "1",
    visitor: "2020.05.14",
    sortation: "이미지",
    phone: "010-****-***9",
    date: "2020.05.14",
    Validity: "30분",
    Approval: "승인"
  },
  {
    id: "2",
    facility: "99",
    visitor: "2020.05.14",
    sortation: "이미지",
    phone: "010-****-***1",
    date: "2020.08.14",
    Validity: "35분",
    Approval: "거절"
  },
  {
    id: "3",
    facility: "80",
    visitor: "2020.05.14",
    sortation: "이미지",
    phone: "010-****-***3",
    date: "2020.07.14",
    Validity: "30분",
    Approval: "승인"
  }
]
const RecomendList = () => {
  const history = useHistory()
  // const [en, seten] = useState(false);
  // const [zn, setzn] = useState(false);
  // const 영어관리스위치 = () => {
  //   seten(!en);
  // };
  let [누른탭, 누른탭변경] = useState(0)
  return (
    <>
      <Breadcrumb title="추천 가이드관리" parent="추천 가이드리스트" />
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm-12">
            <div className="card">
              <div className="card-header">
                <div className="bennerContent">
                  <h5>추천 가이드리스트</h5>
                </div>

                <div className="card-body">
                  <div className="nav-sub">
                    <Nav
                      variant="tabs"
                      defaultActiveKey="link-0"
                      className="nav--sub2"
                    >
                      <div className="navtab">
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-0"
                            onClick={() => {
                              누른탭변경(0)
                            }}
                          >
                            영문관리
                          </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-1"
                            onClick={() => {
                              누른탭변경(1)
                            }}
                          >
                            중문관리
                          </Nav.Link>
                        </Nav.Item>
                      </div>
                      <div>
                        <button
                          id="maching-btn"
                          className="btn btn-danger btn-sm"
                          onClick={() => {
                            history.push("/recomendguidecnt")
                          }}
                        >
                          상품연결
                        </button>
                      </div>
                    </Nav>
                    <TabContent 누른탭={누른탭} />
                  </div>
                </div>
