import React, { useState, useCallback } from "react"
import "../assets/css/layout.css"

import Breadcrumb from "../components/common/breadcrumb"
import axios from "axios"
import CKEditors from "react-ckeditor-component"
import Cookie from "js-cookie"

const AddNotiUser = (props) => {
  // const [inputs, setInputs] = useState({
  //   zh_title: "",
  //   en_title: "",
  //   zn_content: "",
  //   en_content: "",
  // })

  const [ZhTitle, setZhTitle] = useState("")
  const [EnTitle, setEnTitle] = useState("")
  const [znContent, setznContent] = useState("")
  const [enContent, setenContent] = useState("")
  const onSubmithandler = (e) => {
    e.preventDefault()

    const form = new FormData()
    console.log(Cookie.get("token"))
    form.append("zh_title", ZhTitle)
    form.append("en_Title", EnTitle)
    form.append("en_content", enContent)
    form.append("zh_content", znContent)

    const header = {
      Authorization: Cookie.get("token"),
    }

    axios.post("/api/v1//notice/addU", header, form)
  }
  // const { zh_title, en_title, zn_content, en_content } = inputs

  const onEnContentHandler = (e) => {
    const content = e.editor.getData()

    setenContent(content)
  }

  const onZhContentHandler = (e) => {
    const content = e.editor.getData()

    setznContent(content)
  }
  // let form = new FormData()
  // form.append("zh_title", zh_titleState)
  // form.append("en_title,", en_titleState)
  // axios
  //   .post("http://192.168.0.52:49156/api/v1/notice/addU", from)
  //   .then((response) => {
  //     console.log("response : ", JSON.stringify(response, null, 2))
  //   })
  //   .catch((error) => {
  //     console.log("failed", error)
  //   })
  const EnText = useCallback(() => {
    return (
      <div className="col-sm-12">
        <div className="card">
          <div className="card-header">
            <h5>영문내용입력하기</h5>
          </div>
          <div className="card-body">
            <CKEditors
              editor={CKEditors}
              content={enContent}
              events={{
                change: onEnContentHandler,
              }}
            />
          </div>
        </div>
      </div>
    )
  }, [])
  // const EnText = () => (

  // )

  const ZnText = () => (
    <div className="col-sm-12">
      <div className="card">
        <div className="card-header">
          <h5>중문내용입력하기</h5>
        </div>
        <div className="card-body">
          <CKEditors
            editor={CKEditors}
            content={znContent}
            events={{
              change: onZhContentHandler,
            }}
          />
        </div>
      </div>
    </div>
  )
  return (
    <>
      <Breadcrumb title="유저용공지사항" parent="공지사항관리" />
      <div className="card">
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="card-body">
              <div className="noti-title">
                <form>
                  <h5>제목입력</h5>
                  <input
                    onChange={(e) => setEnTitle(e.target.value)}
                    value={EnTitle}
                    class="form-control"
                    id="exampleFormControlInput1"
                    type="input"
                    placeholder="영문으로입력하세요"
                  ></input>
                  <p></p>
                  <input
                    onChange={(e) => setZhTitle(e.target.value)}
                    value={ZhTitle}
                    class="form-control"
                    id="exampleFormControlInput1"
                    type="input"
                    placeholder="중문으로입력하세요"
                  ></input>
                </form>
              </div>
            </div>
            <div className="col-sm-12">
              <div></div>
            </div>
            <div className="col-sm-12">{EnText}</div>
            <div className="col-sm-12">
              <div>
                {/* <CkEditerZn onChange={onEnContentHandler} /> */}
                <ZnText />
                <div>
                  <button
                    className="btn btn-square btn-warning btn-sm"
                    type="button"
                  >
                    삭제
                  </button>
                  &nbsp; &nbsp;
                  {/* <button className="edit-button">수정</button>{' '} */}
                  <button
                    className="btn btn-square btn-secondary active btn-sm"
                    onClick={onSubmithandler}
                  >
                    작성
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default React.memo(AddNotiUser)
