import React, { useState } from "react"
import { keyframes } from "styled-components"
import "../assets/css/layout.css"
import { Link } from "react-router-dom"
import filtersReducer from "../reducers/filters.reducer"
import { TabContent } from "reactstrap"
import Breadcrumb from "../components/common/breadcrumb"
import { Nav } from "react-bootstrap"
const Hashtag = () => {
  const [en, seten] = useState(0)

  const 내가원하는 = () => {}

  return (
    <>
      <Breadcrumb title="해시태그관리" parent="해시태그관리" />
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm-12">
            <div className="card">
              <div className="card-header">
                <div className="bennerContent">
                  <h5>검색 해시태그</h5>
                </div>

                <div className="card-body">
                  <Nav
                    variant="tabs"
                    defaultActiveKey="link-0"
                    className="nav--sub2"
                  >
                    <div className="navtab">
                      <Nav.Item>
                        <Nav.Link
                          eventKey="link-0"
                          onClick={() => {
                            seten(0)
                          }}
                        >
                          영문관리
                        </Nav.Link>
                      </Nav.Item>
                      <Nav.Item>
                        <Nav.Link
                          eventKey="link-1"
                          onClick={() => {
                            seten(1)
                          }}
                        >
                          중문관리
                        </Nav.Link>
                      </Nav.Item>
                    </div>
                  </Nav>
                </div>
                <TabContentTab en={en} />
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}
function TabContentTab(props) {
  if (props.en === 0) {
    return (
      <div className="tag-tab-box">
        <Enup />
      </div>
    )
  } else if (props.en === 1) {
    return (
      <div className="tag-tab-box">
        <Znup />
      </div>
    )
  } else if (props.누른탭 === 2) {
    return <div>내용2</div>
  }
}
const Znup = () => {
  const [teg, settag] = useState([])
  const [usertext, setusertxt] = useState("")

  const addClick = () => {
    const arrayCopy = teg.concat(usertext)
    // arrayCopy;
    settag(arrayCopy)
    setusertxt("")
  }

  return (
    <>
      <div className="tag-total">
        <div style={{ width: "100px", fontWeight: "bold" }}>해시태그추가</div>{" "}
        &nbsp;
        <div className="tag-add-box">
          {/* {alert('한글자 이상 입력해주시기바랍니다')} */}
          <input
            className="form-control"
            maxLength="5"
            placeholder="중문입력"
            onChange={(e) => {
              setusertxt(e.target.value)
            }}
            value={usertext}
            onKeyDown={(e) => {
              if (e.key === "Enter") {
                addClick()
              }
            }}
          />
        </div>{" "}
        &nbsp;
        <div>
          <button className="btn btn-primary btn-sm" onClick={addClick}>
            추가
          </button>
        </div>
      </div>

      <div class="container">
        <div className="row">
          {teg.map((d, i) => (
            <div className="tag-box" key={i}>
              <p>
                {" "}
                <button
                  className="close"
                  onClick={() => {
                    ""
                  }}
                >
                  <span aria-hidden="true">&times;</span>
                </button>
                <div className="tag-item">{d}</div>
              </p>
            </div>
          ))}
        </div>
      </div>
    </>
  )
}
const Enup = () => {
  const [teg, settag] = useState([])
  const [usertext, setusertxt] = useState("")

  const addClick = () => {
    const arrayCopy = teg.concat(usertext)
    // arrayCopy;
    settag(arrayCopy)
    setusertxt("")
  }

  return (
    <>
      <div className="tag-total">
        <div style={{ width: "100px", fontWeight: "bold" }}>해시태그추가</div>{" "}
        &nbsp;
        <div className="tag-add-box">
          {/* {alert('한글자 이상 입력해주시기바랍니다')} */}
          <input
            className="form-control"
            maxLength="5"
            placeholder="영문입력"
            onChange={(e) => {
              setusertxt(e.target.value)
            }}
            value={usertext}
            onKeyDown={(e) => {
              if (e.key === "Enter") {
                addClick()
              }
            }}
          />
        </div>{" "}
        &nbsp;
        <div>
          <button className="btn btn-primary btn-sm" onClick={addClick}>
            추가
          </button>
        </div>
      </div>

      <div class="container">
        <div className="row">
          {teg.map((d, i) => (
            <div className="tag-box" key={i}>
              <p>
                {" "}
                <button
                  className="close"
                  onClick={() => {
                    ""
                  }}
                >
                  <span aria-hidden="true">&times;</span>
                </button>
                <div className="tag-item">{d}</div>
              </p>
            </div>
          ))}
        </div>
      </div>
    </>
  )
}
export default React.memo(Hashtag)
