import React, { useState, useEffect } from "react"
import { keyframes } from "styled-components"
import "../assets/css/layout.css"
import Breadcrumb from "../components/common/breadcrumb"
import { Nav } from "react-bootstrap"
import axios from "axios"
import Cookie from "js-cookie"

// import { Link } from "react-router-dom"
// import filtersReducer from "../reducers/filters.reducer"
// import { TabContent } from "reactstrap"

const Hashtag = () => {
  const [en, seten] = useState(0)

  const 내가원하는 = () => {}

  return (
    <>
      <Breadcrumb title="해시태그관리" parent="해시태그관리" />
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm-12">
            <div className="card">
              <div className="card-header">
                <div className="bennerContent">
                  <h5>검색 해시태그</h5>
                </div>

                <div className="card-body">
                  <Nav
                    variant="tabs"
                    defaultActiveKey="link-0"
                    className="nav--sub2"
                  >
                    <div className="navtab">
                      <Nav.Item>
                        <Nav.Link
                          eventKey="link-0"
                          onClick={() => {
                            seten(0)
                          }}
                        >
                          영문관리
                        </Nav.Link>
                      </Nav.Item>
                      <Nav.Item>
                        <Nav.Link
                          eventKey="link-1"
                          onClick={() => {
                            seten(1)
                          }}
                        >
                          중문관리
                        </Nav.Link>
                      </Nav.Item>
                    </div>
                  </Nav>
                </div>
                <TabContentTab en={en} />
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}
function TabContentTab(props) {
  if (props.en === 0) {
    return (
      <div className="tag-tab-box">
        <Enup />
      </div>
    )
  } else if (props.en === 1) {
    return (
      <div className="tag-tab-box">
        <Znup />
      </div>
    )
  } else if (props.누른탭 === 2) {
    return <div>내용2</div>
  }
}
const Znup = () => {
  const [teg, settag] = useState([])
  const [usertext, setusertxt] = useState("")

  const addClick = () => {
    if (!usertext) {
      alert("한글자이상입력하세요")
      return false
    }
    const arrayCopy = teg.concat(usertext)
    // arrayCopy;
    settag(arrayCopy)
    setusertxt("")
  }

  return (
    <>
      <div className="tag-total">
        <div style={{ width: "100px", fontWeight: "bold" }}>해시태그추가</div>{" "}
        &nbsp;
        <div className="tag-add-box">
          {/* {alert('한글자 이상 입력해주시기바랍니다')} */}
          <input
            className="form-control"
            maxLength="5"
            placeholder="중문입력"
            onChange={e => {
              setusertxt(e.target.value)
            }}
            value={usertext}
            onKeyDown={e => {
              if (e.key === "Enter") {
                addClick()
              }
            }}
          />
        </div>{" "}
        &nbsp;
        <div>
          <button className="btn btn-primary btn-sm" onClick={addClick}>
            추가
          </button>
        </div>
      </div>

      <div class="container">
        <div className="row">
          {teg.map((d, i) => (
            <div className="tag-box" key={i}>
              <p>
                {" "}
                <button
                  className="close"
                  onClick={() => {
                    ""
                  }}
                >
                  <span aria-hidden="true">&times;</span>
                </button>
                <div className="tag-item">{d}</div>
              </p>
            </div>
          ))}
        </div>
      </div>
    </>
  )
}
const Enup = () => {
  const [tegList, setTagList] = useState([])
  const [usertext, setusertxt] = useState("")

  useEffect(() => {
    let form = new FormData()
    form.append("lang", 1)
    axios
      .post("/api/v1/hashtag/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        if (response.data.status === "success") {
          setTagList(response.data.data)
          console.log(response)
        }
      })
      .catch(err => {
        alert("태그를 불러오는데 실패했습니다.")
      })
  }, [])

  const addClick = async e => {
    if (!usertext) {
      alert("한글자이상입력하세요")
      return
    }
    const arrayCopy = tegList.concat(usertext)

    setTagList(arrayCopy)
    setusertxt("")
    // let form = new FormData()

    // form.append("lang", 1)
    // form.append("name", usertext)

    // const cookie = Cookie.get("token")

    // try {
    //   const { data } = await axios.post("/api/v1/hashtag/add", form, {
    //     headers: {
    //       Authorization: cookie
    //     }
    //   })
    //   if (data.status == "success") {
    //     alert("성공적으로 작성되었습니다.")
    //     // history.push("/notice/guide")
    //   }
    // } catch (error) {
    //   alert("작성하는데 실패했습니다.")
    // }
  }

  return (
    <>
      <div className="tag-total">
        <div style={{ width: "100px", fontWeight: "bold" }}>해시태그추가</div>{" "}
        &nbsp;
        <div className="tag-add-box">
          {/* {alert('한글자 이상 입력해주시기바랍니다')} */}
          <input
            className="form-control"
            maxLength="5"
            placeholder="영문입력"
            onChange={e => {
              setusertxt(e.target.value)
            }}
            value={usertext}
            onKeyDown={e => {
              if (e.key === "Enter") {
                addClick()
              }
            }}
          />
        </div>{" "}
        &nbsp;
        <div>
          <button className="btn btn-primary btn-sm" onClick={addClick}>
            추가
          </button>
        </div>
      </div>

      <div class="container">
        <div className="row">
          {tegList.map((d, i) => (
            <div className="tag-box" key={i}>
              <p>
                <button
                  className="close"
                  onClick={() => {
                    ""
                  }}
                >
                  <span aria-hidden="true">&times;</span>
                </button>
                <div className="tag-item">{d.name}</div>
              </p>
              {console.log(d.name)}
            </div>
          ))}
        </div>
      </div>
    </>
  )
}
export default React.memo(Hashtag)
