                      <th scope="col" />
                    </tr>
                  </thead>
                  <tbody>
                    {waitlsit.map((lists, index) => {
                      return (
                        <tr>
                          <th scope="row" key={index}>
                            {index}
                          </th>
                          <td>{lists.status}</td>
                          <td>{lists.registered}</td>
                          <td> {lists.type}</td>
                          <td>
                            {" "}
                            <img
                              src={lists.rep_image}
                              alt="Gallery"
                              className="img-60 b-r-15"
                            />
                          </td>
                          <td>
                            {" "}
                            {lists.en_name}
                            <br />
                            {lists.zh_name}
                          </td>
                          <td>
                            <p>{lists.product}</p>
                            <p>{lists.product}</p>
