import React, { useState, useEffect } from "react"
import { useHistory } from "react-router-dom"
import "../assets/css/layout.css"
import { Nav } from "react-bootstrap"
import Breadcrumb from "../components/common/breadcrumb"
import axios from "axios"
import Cookie from "js-cookie"
import Pagenation from "../util/Pagenation"
import SweetAlert from "react-bootstrap-sweetalert"
import { message } from "antd"
const RecomendList = () => {
  const history = useHistory()
  const [en, seten] = useState(false)
  const [zn, setzn] = useState(false)
  const 영어관리스위치 = () => {
    seten(!en)
  }
  let [누른탭, 누른탭변경] = useState(0)
  return (
    <>
      <Breadcrumb title="추천 가이드 관리" parent="메인관리" />
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm-12">
            <div className="card">
              <div className="card-header">
                <div className="bennerContent">
                  <h5>추천 가이드상품 리스트</h5>
                </div>
                <div className="card-body">
                  <div className="nav-sub">
                    <Nav
                      variant="tabs"
                      defaultActiveKey="link-0"
                      className="nav--sub2"
                    >
                      <div className="navtab">
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-0"
                            onClick={() => {
                              누른탭변경(0)
                            }}
                          >
                            영문관리
                          </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-1"
                            onClick={() => {
                              누른탭변경(1)
                            }}
                          >
                            중문관리
                          </Nav.Link>
                        </Nav.Item>
                      </div>
                    </Nav>
                    <TabContent 누른탭={누른탭} />
                  </div>
                </div>
              </div>
              {/* <div className="card-block row">
<div className="col-sm-12 col-lg-12 col-xl-12"></div>
</div> */}
            </div>
          </div>
        </div>
      </div>
    </>
  )
}
const RecomendItemEn = () => {
  const [isOpen, setIsOpen] = useState(false)
  const [photoIndex, setPhotoIndex] = useState(0)
  const [enlist, setEnlist] = useState([])
  const [indexNum, setIndexNum] = useState(0)
  const [isShow, setIsShow] = useState(false)
  const [listNomber, setListNomber] = useState(0)
  const history = useHistory()
  useEffect(() => {
    let form = new FormData()
    form.append("index", 1)
    form.append("lang", "en")
    axios
      .post("/api/v1/recommend/listG", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        console.log(response)
        if (response.data.status === "success") {
          setEnlist({
            list: response.data.data.list,
            count: response.data.data.count
          })
        }
      })
      .catch(err => {
        message.warning("불러오기실패")
      })
  }, [])
  const UpdateBtn = recNo => {
    history.push(`/recomdoned/onday/update/${recNo}`)
  }
  const fn = async d => {
    let form = new FormData()
    form.append("index", d)
    form.append("lang", "en")
    try {
      const { data } = await axios.post("/api/v1/recommend/listG", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      if (data.status == "success") {
        setEnlist({
          list: data.data.list,
          count: data.data.count
        })
      }
    } catch (error) {
      message.warning("페이지를 로딩하는데 실패했어요.")
    }
  }
  const onNobtn = () => {
    setIsShow(!isShow)
  }
  const alertHandler = okay => {
    setIsShow(!isShow)
    if (okay) {
      let form = new FormData()
      form.append("rec_no", listNomber)
      console.log()
      axios
        .post("api/v1/recommend/deleteG", form, {
          headers: { Authorization: Cookie.get("token") }
        })
        .then(response => {
          // const listse = zhlist.list.filter((item, i) => i !== indexNum)
          setEnlist({
            ...enlist,
            list: enlist.list.filter((item, i) => i !== indexNum)
          })
        })
    }
  }
  return (
    <>
      <div className="cnct-btn">
        <button
          id="maching-btn"
          className="btn btn-danger btn-sm"
          onClick={() => {
            history.push("/recomend/guidecnt/en")
          }}
        >
          상품연결
        </button>
      </div>
      <div className="table-responsive">
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th scope="col" />
              <th scope="col" />
              <th scope="col">노출순위</th>
              <th scope="col">가이드정보</th>
              <th scope="col" />
            </tr>
          </thead>
          <tbody>
            {enlist.list &&
              enlist.list.map((lists, index) => {
                return (
                  <tr>
                    <td scope="row" key={lists.rec_no} />
                    <td>{index}</td>
                    <td>{lists.z_index}</td>{" "}
                    <td>
                      {" "}
                      <img
                        src={lists.profile_img}
                        alt="Gallery"
                        className="img-50 b-r-15"
                        onClick={() => {
                          setIsOpen(true)
                          setPhotoIndex(1)
                        }}
                      />
                      이름 :{lists.guide_name}/나이{lists.age}/언어:
                      {lists.lang}
                      {lists.zh_name}
                    </td>
                    <td>
                      <button
                        className="btn btn-warning active btn -xs btn-sm"
                        type="button"
                        onClick={() => {
                          onNobtn()
                          setIndexNum(index)
                          setListNomber(lists.rec_no)
                          // deleteList(lists.rec_no, index)
                        }}
                      >
                        삭제
                      </button>
                      &nbsp; &nbsp;
                      <button
                        className="btn btn-secondary btn-sm"
                        onClick={() => UpdateBtn(lists.rec_no)}
                      >
                        수정
                      </button>
                    </td>
                  </tr>
                )
              })}
          </tbody>
        </table>
        <div className="pagenation-box">
          <Pagenation data={enlist} fn={fn} />
        </div>
      </div>
      <SweetAlert
        showCancel
        confirmBtnBsStyle="danger"
        danger
        show={isShow}
        onConfirm={() => alertHandler(true)}
        onCancel={() => alertHandler(false)}
        cancelBtnBsStyle="default"
        title="리스트삭제"
      >
        삭제하시겠습니까?
      </SweetAlert>
    </>
  )
}
