import React, { useState, useEffect } from "react"
import "../assets/css/layout.css"
import { Editor } from "react-draft-wysiwyg"
import "./editer.css"
import { EditorState, convertToRaw } from "draft-js"

import Breadcrumb from "../components/common/breadcrumb"
import axios from "axios"
import CKEditors from "react-ckeditor-component"
import Cookie from "js-cookie"
import DraftEditer from "../draftEditer/DraftEditer"
const NotiGuideUpDate = ({ history, match }) => {
  const [KoTitle, setKoTitle] = useState("")
  const [KoContent, setKoContent] = useState("")
  const [editorState, setEditorState] = useState(EditorState.createEmpty())

  const onEditorStateChange = data => {
    setKoContent(data)
  }

  useEffect(() => {
    let form = new FormData()

    form.append("notice_no", match.params.id)

    // 데이터 가져오기
    axios
      .post("/api/v1/notice/editpageG", form, {
        headers: { Authorization: Cookie.get("token") }
      })
      .then(function(response) {
        // 데이터불러오는데 성공했는지 확인 if
        setKoContent(response.data.data.editor)
        setKoTitle(response.data.data.title)
      })
    // 데이터 가져온후에 state 바인딩
  }, [])
  const onKoContentHandler = e => {
    const content = e.editor.getData()

    setKoContent(content)
  }
  const onSubmithandler = async e => {
    e.preventDefault()
    let form = new FormData()
    form.append("title", KoTitle)
    form.append("editor", KoContent)
    form.append("notice_no", match.params.id)

    const cookie = Cookie.get("token")

    try {
      const { data } = await axios.post("/api/v1/notice//editG", form, {
        headers: {
          Authorization: cookie
        }
      })
      if (data.status == "success") {
        alert("성공적으로 수정되었습니다.")
        history.push("/notice/guide")
        // history.push("/notice/guide")
      }
    } catch (error) {
      alert("수정하는데 실패했습니다.")
    }
  }
  const KoText = () => {
    // console.log("????")
    return (
      <div className="col-sm-12">
        <div className="card">
          <div className="card-header" />
          <div className="card-body" />
        </div>
      </div>
    )
  }

  const onUpload = files => {
    const form = new FormData()

    form.append("type", "A")
    form.append("file", files)

    return axios
      .post("/api/v1/file/uploadFile", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(res => {
        return { data: { link: res.data.data } }
      })
      .catch(err => {
        console.log(err)
      })
  }
  return (
    <>
      <Breadcrumb title="가이드용 공지사항 수정페이지" parent="공지사항관리" />
      <div className="card">
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="card-body">
              <div className="noti-title">
                <form>
                  <h5>제목입력</h5>
                  <input
                    onChange={e => setKoTitle(e.target.value)}
                    value={KoTitle}
                    class="form-control"
                    id="exampleFormControlInput1"
                    type="input"
                    placeholder="한글로입력해주세요"
                  />
                </form>
              </div>
            </div>
            <div className="col-sm-12">
              <div />
            </div>
            <div className="col-sm-12">
              <Editor
                editorState={editorState}
                toolbarClassName="toolbarClassName"
                wrapperClassName="home-wrapper"
                editorClassName="home-editor"
                onEditorStateChange={onEditorStateChange}
                toolbar={{
                  // inline: { inDropdown: true },
                  // list: { inDropdown: true },
                  // textAlign: { inDropdown: true },
                  // link: { inDropdown: true },
                  // history: { inDropdown: true },
                  image: { uploadCallback: onUpload }
                }}
                localization={{
                  locale: "ko"
                }}
              />
            </div>
            <div className="col-sm-12">
              <div>
                <div>
                  <button
                    className="btn btn-square btn-warning btn-sm"
                    type="button"
                  >
                    삭제
                  </button>
                  &nbsp; &nbsp;
                  {/* <button className="edit-button">수정</button>{' '} */}
                  <button
                    className="btn btn-square btn-secondary active btn-sm"
                    type="button"
                    onClick={onSubmithandler}
                  >
                    수정하기
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default NotiGuideUpDate
