import React, { useState, useEffect } from "react"
import "../assets/css/layout.css"
import Breadcrumb from "../components/common/breadcrumb"
import one from "../assets/images/user/1.jpg"
import axios from "axios"
import Cookie from "js-cookie"
import { useHistory } from "react-router-dom"
import RecomendList from "./RecommedList"

const RcomendOneDayUpdate = ({ match }) => {
  const [connectList, setconnectList] = useState([])
  const [searchText, setSearchText] = useState("")
  const [guideList, setguideList] = useState([])
  const [ViewLank, setViewLank] = useState(0)
  const history = useHistory()

  useEffect(() => {
    let form = new FormData()
    console.log(match.params.id)
    form.append("rec_no", match.params.id)

    // 데이터 가져오기
    axios
      .post("/api/v1/recommend/editPageT", form, {
        headers: { Authorization: Cookie.get("token") }
      })
      .then(function(response) {
        setViewLank(response.data.data.z_index)
        setconnectList(response.data.data.tourData)

        console.log(response.data.data.z_index)
        // 데이터불러오는데 성공했는지 확인 if
      })
    // 데이터 가져온후에 state 바인딩
  }, [])

  const searchhandler = () => {
    if (!searchText) {
      alert("한글자이상입력하세요")
      return
    }

    const form = new FormData()
    form.append("index", 1)
    form.append("lang", "en")
    form.append("search", searchText)

    axios
      .post("/api/v1/recommend/searchT", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        console.log(response)
        if (response.data.status === "success") {
          // if (response.data.data.count > 0) {
          setguideList(response.data.data.list)
          // } else {
          //   alert("일치하는 가이드가 없습니다")
          //   setguideList([])
          // }
          console.log(guideList)
        }
      })
      .catch(err => {
        alert("검색한데이터를 불러오지못했습니다")
      })
  }

  const addBtn = (name, img, tour_no) => {
    const userInfo = {
      name,
      img,
      tour_no
    }
    setconnectList([userInfo])
  }

  const onSubmithandler = async e => {
    if (connectList.length <= 0) {
      alert(" 연결할 가이드를검색후 추가해주세요")
      return
    } else if (ViewLank === 0) {
      alert("노출순위는 1이상 지정해주세요")
      return
    }

    let form = new FormData()

    form.append("z_index", ViewLank)
    form.append("lang", "en")
    form.append("tour_no", connectList[0].tour_no)

    const cookie = Cookie.get("token")
    try {
      const { data } = await axios.post("/api/v1/recommend/addT", form, {
        headers: {
          Authorization: cookie
        }
      })
      if (data.status === "success") {
        alert("성공적으로 작성되었습니다.")
        history.push("/recommendlist")
      }
    } catch (error) {
      alert("작성하는데 실패했습니다.")
    }
  }
  const onRemove = () => {
    setconnectList([])
  }
  const onReset = () => {
    setconnectList([])
    setSearchText("")
    setguideList([])
  }

  return (
    <div>
      <Breadcrumb title="추천 원데이투어 연결하기" parent="추천 가이드 관리" />

      <div className="col-sm-12">
        <div className="card">
          <div className="card-body">
            <div className=" oneday-cont">
              <div>
                {/* <h3>추천 원데이투어 상품연결</h3> */}

                {/* <div className="oneday-sub-bar">
                  <h3>노출순위</h3>
                  <div>
                    <input
                      className=" form-control"
                      type="number"
                      min="1"
                      max="99"
                      defaultValue="0"
                      value={ViewLank}
                      onChange={e => setViewLank(e.target.value)}
                    />
                  </div>
                </div> */}
                {/* <div className="onday-total-box">
                  <div className="guide-left-box">
                    <div className="avatar ratio">
                      <img
                        className="b-r-8 height-80"
                        src={inFolist.img}
                        alt="#"
                      />
                    </div>
                    &nbsp; &nbsp;
                    <h2>가이드명:{inFolist.name}/나이:/언어:/</h2>
                  </div>
                  <div className="onday-right-box">
                    <button
                      className="btn btn-warning active btn-sm"
                      type="button"
                      onClick={onRemove}
                    >
                      삭제
                    </button>
                  </div>
                </div> */}
                {connectList &&
                  connectList.map((inFolist, index) => (
                    <>
                      <h3>추천 원데이투어 상품연결</h3>

                      <div className="oneday-sub-bar">
                        <h3>노출순위</h3>
                        <div>
                          <input
                            className=" form-control"
                            type="number"
                            min="1"
                            max="99"
                            defaultValue="0"
                            onChange={e => setViewLank(e.target.value)}
                          />
                        </div>
                      </div>
                      <div className="onday-total-box">
                        <div className="guide-left-box">
                          <div className="avatar ratio">
                            <img
                              className="b-r-8 height-80"
                              src={inFolist.img}
                            />
                          </div>
                          {console.log(inFolist)}
                          &nbsp; &nbsp;
                          <h2>가이드명:{inFolist.name}/나이:/언어:/</h2>
                        </div>
                        <div className="onday-right-box">
                          <button
                            className="btn btn-warning active btn-sm"
                            type="button"
                            onClick={onRemove}
                          >
                            삭제
                          </button>
                        </div>
                      </div>
                    </>
                  ))}
              </div>
            </div>
            <div className="oneday-seach-box2 ">
              <div className="size-box">
                <input
                  className="form-control"
                  type="text"
                  value={searchText}
                  onChange={e => setSearchText(e.target.value)}
                  placeholder="가이드의 이름을 영어로 입력해주세요"
                  onKeyDown={e => {
                    if (e.key === "Enter") {
                      searchhandler()
                    }
                  }}
                />

                <i className="fa fa-search fa-2x" onClick={searchhandler} />
              </div>

              {guideList &&
                guideList.map((list, index) => (
                  <div className="onday-total-box2">
                    <div className="onday-left-box2">
                      <img
                        className="img-50 b-r-15"
                        src={list.rep_image}
                        alt="#"
                      />
                      &nbsp; &nbsp;
                      <div>
                        price : {list.en_price} $ / Title:{list.en_name}/ Name :
                        {list.guide_name}
                      </div>
                    </div>
                    <div className="oneday-right-box2">
                      <button
                        className="btn btn-secondary btn-sm"
                        type="button"
                        onClick={() =>
                          addBtn(list.en_name, list.rep_image, list.tour_no)
                        }
                      >
                        추가
                      </button>
                    </div>
                  </div>
                ))}
            </div>
            <div className="btn-div">
              <button
                className="btn btn-warning active btn-sm"
                type="button"
                onClick={onReset}
              >
                초기화
              </button>
              &nbsp; &nbsp;
              <button
                className="btn btn-secondary btn-sm"
                onClick={onSubmithandler}
              >
                확인
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

