import React from 'react';
import Data from '../Data';
import '../assets/css/layout.css';
import { useHistory } from 'react-router-dom';
const UserList = () => {
  const [seachtext, setserchText] = React.useState('');
  const [dummyData, setDummyData] = React.useState(Data);
  const SerchFilter = () => {
    const filter = Data.filter((d) => d.nicname.indexOf(seachtext) > -1);
    setDummyData(filter);
  };
  const onRemove = (id) => {
    // user.id 가 파라미터로 일치하지 않는 원소만 추출해서 새로운 배열을 만듬
    // = user.id 가 id 인 것을 제거함
    setserchText(seachtext.filter((user) => user.id !== id));
  };
  const history = useHistory();
  return (
    <div>
      <div className="card">
        <div className="card-header">
          <h5>유저관리</h5>
        </div>
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="user-search">
              <div>
                Seach:
                <input
                  className="search-input"
                  placeholder="닉네임으로 검색하세요"
                  onChange={(e) => setserchText(e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                      SerchFilter();
                    }
                  }}
                />
              </div>
            </div>
            <div className="table-responsive">
              <table className="table">
                <thead className="thead-light">
                  <tr>
                    <th scope="col"></th>
                    <th scope="col">가입일시</th>
                    <th scope="col">소셜구분</th>
                    <th scope="col">언어</th>
                    <th scope="col">이메일</th>
                    <th scope="col">닉네임</th>
                    <th scope="col"></th>
                  </tr>
                </thead>
                <tbody>
                  {dummyData.map((a) => {
                    return (
                      <tr>
                        <th scope="row" key={a.id}>
                          {a.id}
                        </th>
                        <td>{a.date}</td>
                        <td>{a.sns}</td>
                        <td>{a.lang}</td>
                        <td>{a.email}</td>
                        <td>{a.nicname}</td>

                        <td>
                          <button
                            className="user-view-button"
                            onClick={
                              (() => {
                                history.push('/userdetails');
                              },
                              { onRemove })
                            }
                          >
                            보기
                          </button>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default React.memo(UserList);
