        alert("가이드 상품 리스트를  불러오는데 실패했습니다.")
      })
  }, [])
  const onSearchHandler = () => {
    const form = new FormData()
    form.append("index", 1)
    form.append("type", 2)
    form.append("search", seachtext)

    axios
      .post("/api/v1/product/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
