                      }}
                    />
                  </div>
                </div>
                <div className="icon-search">
                  <i className="fa fa-search" />
                </div>
              </div>

              <div className="table-responsive">
                <table className="table">
                  <thead className="thead-light">
                    <tr>
                      <th scope="col" />
                      <th scope="col">승인상태</th>
                      <th scope="col">작성일시</th>
                      <th scope="col">타입</th>
                      <th scope="col">상품정보</th>
                      <th scope="col" />
                      <th scope="col" />
                      <th scope="col" />
                      <th scope="col" />
                    </tr>
                  </thead>
                  <tbody>
                    {waitlsit.map((lists, index) => {
                      return (
                        <tr>
                          <th scope="row" key={index}>
                            {index}
                          </th>
                          <td>{lists.status}</td>
                          <td>{lists.registered}</td>
                          <td> {lists.type}</td>
                          <td>
                            {" "}
                            <img
                              src={lists.rep_image}
                              alt="Gallery"
                              className="img-60 b-r-15"
                            />
                          </td>
                          <td>
                            {" "}
                            {lists.en_name}
                            <br />
                            {lists.zh_name}
                          </td>
                          <td>
                            <p>{lists.product}</p>
                            <p>{lists.product}</p>
                          </td>
                          <td>
                            {lists.option.hour} HOUR 
                            <br />
                            CNY : {lists.option.cny}&nbsp;| &nbsp;USD:{" "}
                            {lists.option.usd}
                          </td>

                          <td>
                            <button
                              className="  btn btn-secondary btn-sm"
                              onClick={() => {
                                history.push(
                                  `/guidegoods/ditail/${lists.product_no}`
                                )
                              }}
                            >
                              보기
                            </button>
                          </td>
                        </tr>
                      )
                    })}
                  </tbody>
                </table>
                <div className="pagenation-box">
                  <Paging waitlsit={waitlsit} />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

