import React, { useContext } from 'react';
import { useHistory } from 'react-router-dom';
import '../assets/css/layout.css';
import Breadcrumb from '../components/common/breadcrumb';

const dummyData = [
  {
    id: '1',
    facility: '1',
    visitor: '2020.05.14',
    sortation: '이미지',
    phone: '010-****-***9',
    date: '2020.05.14',
    Validity: '30분',
    Approval: '승인',
  },
  {
    id: '2',
    facility: '99',
    visitor: '2020.05.14',
    sortation: '이미지',
    phone: '010-****-***1',
    date: '2020.08.14',
    Validity: '35분',
    Approval: '거절',
  },
  {
    id: '3',
    facility: '80',
    visitor: '2020.05.14',
    sortation: '이미지',
    phone: '010-****-***3',
    date: '2020.07.14',
    Validity: '30분',
    Approval: '승인',
  },
];
const BennerList = () => {
  const history = useHistory();
  // let 데이터 = useContext(데이터context);
  return (
    <>
      <Breadcrumb title="배너관리" parent="배너관리" />
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm-12">
            <div className="card">
              <div className="card-header">
                {/* <h5>Sample Card</h5> */}
                <span>
                  {/* lorem ipsum dolor sit amet, consectetur adipisicing elit */}
                </span>
              </div>
              <div className="card-body">
                {/*  */}
                <div className="card-block row">
                  <div className="col-sm-12 col-lg-12 col-xl-12" id="bennerH">
                    <div className="bennerContent">
                      <div className="benner">배너관리</div>
                      <div className="benner">
                        <div className="benner-btn"></div>
                      </div>
                    </div>
                  </div>
                </div>

                <div className="card">
                  <div className="card-header">
                    <h5>배너관리</h5>
                    <button
                      id="bnr-btn"
                      className="btn btn-danger btn-sm"
                      onClick={() => {
                        history.push('/banneradd/');
                      }}
                    >
                      배너등록
                    </button>
                  </div>
                  <div className="card-block row">
                    <div className="col-sm-12 col-lg-12 col-xl-12">
                      <div className="table-responsive">
                        <table className="table">
                          <thead className="thead-light">
                            <tr>
                              <th scope="col"></th>
                              <th scope="col">노출순위</th>
                              <th scope="col">작성일시</th>
                              <th scope="col">대표이미지</th>
                              <th scope="col"></th>
                            </tr>
                          </thead>
                          <tbody>
                            {dummyData.map((a) => {
                              return (
                                <tr>
                                  <th scope="row" key={a.id}>
                                    1
                                  </th>
                                  <td>{a.facility}</td>
                                  <td>{a.date}</td>
                                  <td>{a.sortation}</td>
                                  <td>
                                    <button className="cancel-btn">취소</button>
                                    &nbsp; &nbsp;
                                    <button className="edit-button">
                                      수정
                                    </button>{' '}
                                  </td>
                                </tr>
                              );
                            })}
                          </tbody>
                        </table>
                      </div>
                    </div>
                  </div>
                </div>
                {/*  */}
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default BennerList;
