import React, { useState, useEffect } from "react"
import "../assets/css/layout.css"
import Breadcrumb from "../components/common/breadcrumb"
import one from "../assets/images/user/1.jpg"
import axios from "axios"
import Cookie from "js-cookie"

const RcomendOneDay = () => {
  const [searchText, setSearchText] = useState("")
  const [guideList, setguideList] = useState([])
  const [connectList, setconnectList] = useState([])

  const searchhandler = () => {
    if (!searchText) {
      alert("한글자이상입력하세요")
      return
    }

    const form = new FormData()
    form.append("index", 1)
    form.append("lang", "en")
    form.append("search", searchText)

    axios
      .post("/api/v1/recommend/searchT", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        console.log(response)
        if (response.data.status === "success") {
          if (response.data.data.count > 0) {
            setguideList(response.data.data.list)
          } else {
            alert("일치하는 가이드가 없습니다")
            setguideList([])
          }
        }
      })
      .catch(err => {
        alert("검색한데이터를 불러오지못했습니다")
      })
  }

  const connecthandler = () => {}
  return (
    <div>
      <Breadcrumb title="추천 원데이투어" parent="추천 가이드 관리" />

      <div className="col-sm-12">
        <div className="card">
          <div className="card-body">
            <div className="oneday-sub-bar">
              <h6>노출순위</h6>
              <div>
                <select className="form-control digit">
                  <option value="select">1</option>
                  <option value="First">2</option>
                  <option value="Second">3</option>
                  <option value="Third">4</option>
                </select>
              </div>
            </div>
            <div className=" oneday-cont">
              <h6>추천 원데이투어 상품연결</h6>
              <div>
                <div className="onday-total-box">
                  <div className="guide-left-box">
                    <div className="avatar ratio">
                      <img className="b-r-8 height-80" src={one} alt="#" />
                    </div>
                    &nbsp; &nbsp;<div>가이드명/나이/언어/</div>
                  </div>
                  <div className="onday-right-box">
                    <button
                      className="btn btn-square btn-danger btn-s"
                      type="button"
                    >
                      삭제
                    </button>
                  </div>
                </div>
              </div>
            </div>
            <div className="oneday-seach-box2 ">
              <div className="size-box">
                <input
                  className="form-control"
                  type="text"
                  value={searchText}
                  onChange={e => setSearchText(e.target.value)}
                  placeholder="가이드의 이름을 한글로입력해주세요"
                  onKeyDown={e => {
                    if (e.key === "Enter") {
                      searchhandler()
                    }
                  }}
                />

                <i className="fa fa-search fa-2x" onClick={searchhandler} />
              </div>

              {guideList.map((lists, index) => (
                <div className="onday-total-box2">
                  <div className="onday-left-box2">
                    <img
                      className="img-50 b-r-15"
                      src={lists.rep_image}
                      alt="#"
                    />
                    &nbsp; &nbsp;
                    <div>
                      가격 : {lists.en_price}/상품명누락/Name :
                      {lists.guide_name} 한글명
                    </div>
                  </div>
                  <div className="oneday-right-box2">
                    <button
                      className="btn btn-square btn-warning btn-s"
                      type="button"
                      onChange={connecthandler}
                      value={lists.tour_no}
                    >
                      추가
                    </button>
                    {console.log(connecthandler.value)}
                  </div>
                </div>
              ))}
            </div>
            <div className="btn-div">
              <button
                className="btn btn-square btn-danger btn-sm"
                type="button"
              >
                삭제
              </button>
              &nbsp; &nbsp;
              <button className="btn btn-square btn-light btn-sm ">확인</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
export default RcomendOneDay
