import React, { useState, useEffect } from "react"
import { useHistory } from "react-router-dom"
import "../assets/css/layout.css"
import { Nav } from "react-bootstrap"
import Breadcrumb from "../components/common/breadcrumb"
import PagnationTool from "../PagnationTool"
import axios from "axios"
import Cookie from "js-cookie"
const RecomendList = () => {
  const history = useHistory()
  const [searchText, setSearchText] = useState()

  let [누른탭, 누른탭변경] = useState(0)
  return (
    <>
      <Breadcrumb title="추천 가이드관리" parent="추천 가이드리스트" />
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm-12">
            <div className="card">
              <div className="card-header">
                <div className="bennerContent" />

                <div className="card-body">
                  <div className="nav-sub">
                    <Nav
                      variant="tabs"
                      defaultActiveKey="link-0"
                      className="nav--sub2"
                    >
                      <div className="navtab">
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-0"
                            onClick={() => {
                              누른탭변경(0)
                            }}
                          >
                            영문관리
                          </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-1"
                            onClick={() => {
                              누른탭변경(1)
                            }}
                          >
                            중문관리
                          </Nav.Link>
                        </Nav.Item>
                      </div>
                    </Nav>
                    <TabContent 누른탭={누른탭} />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* <div className="card-block row">
        <div className="col-sm-12 col-lg-12 col-xl-12"></div>
      </div> */}
    </>
  )
}
function TabContent(props) {
  if (props.누른탭 === 0) {
    return (
      <div>
        <RecomendItemEn />
      </div>
    )
  } else if (props.누른탭 === 1) {
    return (
      <div>
        <RecomendItemZn />
      </div>
    )
  } else if (props.누른탭 === 2) {
    return <div>내용2</div>
  }
}

const RecomendItemEn = () => {
  const [enList, setEnList] = useState([])
  const history = useHistory()
  useEffect(() => {
    let form = new FormData()
    form.append("index", 1)
    form.append("lang", "en")
    axios
      .post("/api/v1/recommend/listG", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        if (response.data.status === "success") {
          setEnList(response.data.data.list)
        }
      })
      .catch(err => {
        alert("추천 가이드 리스트를  불러오는데 실패했습니다.")
      })
  }, [])
  const deleteList = (listNo, index) => {
    if (window.confirm("삭제 하시겠습니까?")) {
      console.log(listNo)
      let form = new FormData()
      form.append("rec_no", listNo)
      axios
        .post("api/v1/recommend/deleteG", form, {
          headers: { Authorization: Cookie.get("token") }
        })
        .then(response => {
          const list = enList.filter((item, i) => i !== index)

          setEnList(list)
        })
    }
  }

  return (
    <div className="table-responsive">
      <div className="cnct-btn">
        <button
          id="maching-btn"
          className="btn btn-danger btn-sm"
          onClick={() => {
            history.push("/recomend/guidecnt/en")
          }}
        >
          상품연결
        </button>
      </div>
      <table className="table">
        <thead className="thead-light">
          <tr>
            <th scope="col" />

            <th scope="col">작성일시</th>
            <th scope="col">가이드정보</th>
            <th scope="col" />
          </tr>
        </thead>
        <tbody>
          {enList.map((lists, index) => {
            return (
              <tr>
                <th scope="row" key={index}>
                  {index}
                </th>

                <td>작성일자 데이터에없음</td>
                <td>
                  <img
                    className="img-50 b-r-15"
                    src={lists.profile_img}
                    alt="#"
                  />
                  이름 :{lists.guide_name}/나이{lists.age}/언어:
                  {lists.lang}/위치:데이터에없음
                </td>
                <td>
                  <button
                    className="btn btn-warning active  btn -xs btn-sm"
                    type="button"
