import React, { useState } from 'react';
import { keyframes } from 'styled-components';
import '../assets/css/layout.css';
import { Link } from 'react-router-dom';
const Hashtag = () => {
  const [teg, settag] = useState(['사관', '욕설', '금지', '태그']);
  const [usertext, setusertxt] = useState('');
  const addClick = () => {
    const arrayCopy = teg.concat(usertext);
    // arrayCopy;
    settag(arrayCopy);
    setusertxt('');
  };

  return (
    <div>
      <div class="container">
        <div class="row">
          <div class="col-md-7">
            <h4>검색 해시태그 관리 </h4>
            <div className="lang-box">
              <Link>
                <div>영문관리</div>
              </Link>
              <Link>
                <div>중문관리</div>
              </Link>
            </div>
            <label>
              해시태그추가 &nbsp;
              <input
                placeholder="영문으로입력하세요"
                onChange={(e) => {
                  setusertxt(e.target.value);
                }}
                value={usertext}
                onKeyDown={(e) => {
                  if (e.key === 'Enter') {
                    addClick();
                  }
                }}
              />
            </label>{' '}
            &nbsp;
            <button className="btn btn-primary btn-xs" onClick={addClick}>
              추가
            </button>
          </div>
        </div>
      </div>
      <div class="container">
        <div className="row">
          {teg.map((d, i) => (
            <div className="tag-box" key={i}>
              <div className="tag-item"> {d}</div>
              <button className="close" onClick={() => {}}>
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Hashtag;
