import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import '../assets/css/layout.css';
import { Nav } from 'react-bootstrap';
const dummyData = [
  {
    id: '1',
    facility: '1',
    visitor: '2020.05.14',
    sortation: '이미지',
    phone: '010-****-***9',
    date: '2020.05.14',
    Validity: '30분',
    Approval: '승인',
  },
  {
    id: '2',
    facility: '99',
    visitor: '2020.05.14',
    sortation: '이미지',
    phone: '010-****-***1',
    date: '2020.08.14',
    Validity: '35분',
    Approval: '거절',
  },
  {
    id: '3',
    facility: '80',
    visitor: '2020.05.14',
    sortation: '이미지',
    phone: '010-****-***3',
    date: '2020.07.14',
    Validity: '30분',
    Approval: '승인',
  },
];
const Guidelist = () => {
  const history = useHistory();
  const [en, seten] = useState(false);
  const [zn, setzn] = useState(false);
  const 영어관리스위치 = () => {
    seten(!en);
  };
  let [누른탭, 누른탭변경] = useState(0);
  return (
    <>
      <div className="card-block row">
        <div className="col-sm-12 col-lg-12 col-xl-12" id="bennerH">
          <div className="bennerContent">
            <div className="benner">가이드관리</div>
            <div className="benner">
              <div className="benner-btn">
                <button
                  id="bnr-btn"
                  className="btn btn-danger btn-sm"
                  onClick={() => {
                    history.push('/recommendguide');
                  }}
                >
                  상품연결
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="card">
        <div className="card-header">
          <div className="lang-box">
            <div
              className="en-btn"
              onClick={() => {
                seten(0);
              }}
              style={{ color: en === 0 ? 'red' : 'black' }}
            >
              영문관리
            </div>
            <div
              className="zn-btn"
              onClick={() => {
                seten(1);
              }}
              style={{ color: en === 1 ? 'blue' : 'black' }}
            >
              중문관리
            </div>
          </div>
        </div>
      </div>

      {/* <div className="card-block row">
        <div className="col-sm-12 col-lg-12 col-xl-12"></div>
      </div> */}
    </>
  );
};
function TabContent(props) {
  if (props.누른탭 === 0) {
    return (
      <div>
        <RecomendItemEn />
      </div>
    );
  } else if (props.누른탭 === 1) {
    return (
      <div>
        <RecomendItemZn />
      </div>
    );
  } else if (props.누른탭 === 2) {
    return <div>내용2</div>;
  }
}

const RecomendItemEn = () => {
  return (
    <div className="table-responsive">
      <table className="table">
        <thead className="thead-light">
          <tr>
            <th scope="col"></th>
            <th scope="col">Priority</th>
            <th scope="col">Date</th>
            <th scope="col">Goods</th>
            <th scope="col"></th>
          </tr>
        </thead>
        <tbody>
          {dummyData.map((a) => {
            return (
              <tr>
                <th scope="row" key={a.id}>
                  {a.id}
                </th>
                <td>{a.facility}</td>
                <td>{a.date}</td>
                <td>{a.sortation}</td>
                <td>
                  <button className="cancel-btn">삭제</button>
                  &nbsp; &nbsp;
                  <button className="edit-button">수정</button>{' '}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};
const RecomendItemZn = () => {
  return (
    <div className="table-responsive">
      <table className="table">
        <thead className="thead-light">
          <tr>
            <th scope="col"></th>
            <th scope="col">노출순위</th>
            <th scope="col">작성일시</th>
            <th scope="col">대표이미지</th>
            <th scope="col"></th>
          </tr>
        </thead>
        <tbody>
          {dummyData.map((a) => {
            return (
              <tr>
                <th scope="row" key={a.id}>
                  {a.id}
                </th>

                <td>{a.facility}</td>
                <td>{a.date}</td>
                <td>{a.sortation}</td>
                <td>
                  <button className="cancel-btn">취소</button>
                  &nbsp; &nbsp;
                  <button className="edit-button">수정</button>{' '}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};
export default Guidelist;
