import React, { useState, useEffect } from "react"
import { useHistory } from "react-router-dom"
import "../assets/css/layout.css"
import { Nav } from "react-bootstrap"
import one from "../assets/images/user/1.jpg"
import Breadcrumb from "../components/common/breadcrumb"
import Lightbox from "react-image-lightbox"
import Data from "../Data"
import "react-image-lightbox/style.css"
import PagnationTool from "../PagnationTool"
import axios from "axios"
import Cookie from "js-cookie"

const RecomendList = () => {
  const history = useHistory()
  const [en, seten] = useState(false)
  const [zn, setzn] = useState(false)
  const 영어관리스위치 = () => {
    seten(!en)
  }
  let [누른탭, 누른탭변경] = useState(0)
  return (
    <>
      {/* <div className="card-block row">
        <div className="col-sm-12 col-lg-12 col-xl-12" id="bennerH">
          <div className="bennerContent">
            <div className="benner">원데이투어 관리</div>
            <div className="benner">
              <div className="benner-btn">
                <button
                  id="bnr-btn"
                  className="btn btn-danger btn-sm"
                  onClick={() => {
                    history.push('/recommend');
                  }}
                >
                  상품연결
                </button>
              </div>
            </div>
          </div>
        </div>
      </div> */}
      <Breadcrumb title="추천 원데이투어 관리" parent="추천원데이투어리스트" />
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm-12">
            <div className="card">
              <div className="card-header">
                <div className="bennerContent">
                  <h5>추천원데이투어리스트</h5>
                </div>

                <div className="card-body">
                  <div className="nav-sub">
                    <Nav
                      variant="tabs"
                      defaultActiveKey="link-0"
                      className="nav--sub2"
                    >
                      <div className="navtab">
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-0"
                            onClick={() => {
                              누른탭변경(0)
                            }}
                          >
                            영문관리
                          </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-1"
                            onClick={() => {
                              누른탭변경(1)
                            }}
                          >
                            중문관리
                          </Nav.Link>
                        </Nav.Item>
                      </div>
                    </Nav>

                    <TabContent 누른탭={누른탭} />
                  </div>
                </div>
