import React, { Fragment, useState, useEffect } from 'react';
import { TabContent, TabPane, Nav, NavItem, NavLink } from 'reactstrap';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { useSelector, useDispatch } from 'react-redux';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import { toast } from 'react-toastify';

const ThemeCustomizer = () => {
  const primary_color = localStorage.getItem('primary_color');
  const secondary_color = localStorage.getItem('secondary_color');
  const layout_version = localStorage.getItem('layout_version');
  const sidebar_type = localStorage.getItem('wrapper');
  const body_sidebar_type = localStorage.getItem('bodyWrapper');
  const [modal, setModal] = useState();
  const [rightSidebar, setRightSidebar] = useState(true);
  const [activeTab, setActiveTab] = useState('1');
  const [activeTab1, setActiveTab1] = useState('1');

  const configDB = useSelector((content) => content.Customizer.customizer);
  const color = localStorage.getItem('color');

  const mix_layout = configDB.color.mix_layout;
  const [layout_type, setLayout_type] = useState(configDB.settings.layout_type);
  const dispatch = useDispatch();
  const config_primary = configDB.color.primary_color;
  const config_secondary = configDB.color.secondary_color;
  const config_color = configDB.color.color;
  const config_layout_version = configDB.color.layout_version;
  configDB.settings.sidebar.wrapper = sidebar_type;
  configDB.settings.sidebar.bodyWrapper = body_sidebar_type;

  useEffect(() => {
    dispatch({ type: 'ADD_COSTOMIZER' });

    dispatch({
      type: 'ADD_COLOR',
      payload: {
        color,
        primary_color,
        secondary_color,
        layout_version,
      },
    });

    //set layout_type
    document.body.setAttribute('main-theme-layout', layout_type);
    document.documentElement.dir = layout_type;

    //set sidebar wrapper
    if (sidebar_type === null && body_sidebar_type === null) {
      document.querySelector('.page-wrapper').className =
        'page-wrapper ' + configDB.settings.sidebar.wrapper;
      document.querySelector('.page-body-wrapper').className =
        'page-body-wrapper ' + configDB.settings.sidebar.bodyWrapper;
    } else {
      document.querySelector('.page-wrapper').className =
        'page-wrapper ' + sidebar_type;
      document.querySelector('.page-body-wrapper').className =
        'page-body-wrapper ' + body_sidebar_type;
    }

    //set sidebar setting
    document
      .querySelector('.page-sidebar')
      .setAttribute('sidebar-layout', configDB.settings.sidebar_setting);

    //set colors
    document.body.className = mix_layout;
    document.documentElement.className = color;

    if (
      localStorage.getItem('primary_color') == null ||
      localStorage.getItem('secondary_color') == null ||
      localStorage.getItem('color') == null ||
      localStorage.getItem('layout_version') == null
    ) {
      document.documentElement.className = config_color;
      localStorage.setItem('primary_color', config_primary);
      localStorage.setItem('secondary_color', config_secondary);
      localStorage.setItem('color', config_color);
      localStorage.setItem('layout_version', config_layout_version);
      dispatch({
        type: 'ADD_COLOR',
        payload: {
          color: config_color,
          primary_color: config_primary,
          secondary_color: config_secondary,
          layout_version: config_layout_version,
        },
      });
    }

    if (
      sidebar_type === 'compact-wrapper' ||
      configDB.settings.sidebar.wrapper === 'compact-wrapper'
    ) {
      document.querySelector('.compactLogo').className = 'compactLogo show';
    } else {
      document.querySelector('.compactLogo').className = 'compactLogo hide';
    }
    // eslint-disable-next-line
  }, []);

  const toggle = () => {
    setModal(!modal);
  };

  const openCustomizer = () => {
    if (rightSidebar) {
      setRightSidebar(!rightSidebar);
      document.querySelector('.customizer-contain').classList.add('open');
      document.querySelector('.customizer-links').classList.add('open');
    }
  };

  const closeCustomizer = () => {
    setRightSidebar(!rightSidebar);
    document.querySelector('.customizer-contain').classList.remove('open');
    document.querySelector('.customizer-links').classList.remove('open');
  };

  const handleSidebarColor = (e) => {
    document.querySelectorAll('.sidebar-bg-settings li').forEach((item) => {
      item.classList.remove('active');
    });
    document.querySelector('.page-sidebar').className =
      'page-sidebar ' + e.target.classList.value;
    e.target.classList.add('active');
  };

  const handleLayout = (layout) => {
    setLayout_type(layout);
    document.querySelectorAll('.main-layout li').forEach((item) => {
      item.classList.remove('active');
    });
    document.body.setAttribute('main-theme-layout', layout);
    document.documentElement.dir = layout;
    dispatch({ type: 'ADD_LAYOUT', payload: layout });
  };
  const handleSidebarSetting = (e) => {
    e.preventDefault();
    document.querySelectorAll('.sidebar-setting li').forEach((item) => {
      item.classList.remove('active');
    });
    document
      .querySelector('.page-sidebar')
      .setAttribute(
        'sidebar-layout',
        e.currentTarget.getAttribute('data-attr')
      );
    e.currentTarget.classList.add('active');
    dispatch({
      type: 'ADD_SIDEBAR_SETTINGS',
      payload: e.currentTarget.getAttribute('data-attr'),
    });
  };

  const handleCustomizerMix = (e) => {
    e.preventDefault();
    document.querySelectorAll('.customizer-mix li').forEach((item) => {
      item.classList.remove('active');
    });
    document.body.className = e.currentTarget.getAttribute('data-attr');
    e.currentTarget.classList.add('active');
    dispatch({
      type: 'ADD_MIXlAYOUT',
      payload: e.currentTarget.getAttribute('data-attr'),
    });
  };

  const handleSidebarType = (e, wrapper, bodyWrapper) => {
    e.preventDefault();
    document.querySelectorAll('.sidebar-type li').forEach((item) => {
      item.classList.remove('active');
    });
    document.querySelector('.page-wrapper').className =
      'page-wrapper ' + wrapper;
    document.querySelector('.page-body-wrapper').className =
      'page-body-wrapper ' + bodyWrapper;
    e.currentTarget.classList.add('active');
    dispatch({ type: 'ADD_SIDEBAR_TYPES', payload: { wrapper, bodyWrapper } });

    localStorage.setItem('wrapper', wrapper);
    localStorage.setItem('bodyWrapper', bodyWrapper);
    window.location.reload();
  };

  const colorChangeTheme = (value) => {
    if (value === 'light-1') {
      localStorage.setItem('color', 'color-1');
      localStorage.setItem('layout_version', 'light');
      localStorage.setItem('primary_color', '#4466f2');
      localStorage.setItem('secondary_color', '#1ea6ec');
    }
    if (value === 'light-2') {
      localStorage.setItem('color', 'color-2');
      localStorage.setItem('layout_version', 'light');
      localStorage.setItem('primary_color', '#0288d1');
      localStorage.setItem('secondary_color', '#26c6da');
    }
    if (value === 'light-3') {
      localStorage.setItem('color', 'color-3');
      localStorage.setItem('layout_version', 'light');
      localStorage.setItem('primary_color', '#8e24aa');
      localStorage.setItem('secondary_color', '#ff6e40');
    }
    if (value === 'light-4') {
      localStorage.setItem('color', 'color-4');
      localStorage.setItem('layout_version', 'light');
      localStorage.setItem('primary_color', '#4c2fbf');
      localStorage.setItem('secondary_color', '#2e9de4');
    }
    if (value === 'light-5') {
      localStorage.setItem('color', 'color-5');
      localStorage.setItem('layout_version', 'light');
      localStorage.setItem('primary_color', '#7c4dff');
      localStorage.setItem('secondary_color', '#7b1fa2');
    }
    if (value === 'light-6') {
      localStorage.setItem('color', 'color-6');
      localStorage.setItem('layout_version', 'light');
      localStorage.setItem('primary_color', '#3949ab');
      localStorage.setItem('secondary_color', '#4fc3f7');
    }
    if (value === 'dark-1') {
      localStorage.setItem('color', 'color-1');
      localStorage.setItem('layout_version', 'dark-only');
      localStorage.setItem('primary_color', '#4466f2');
      localStorage.setItem('secondary_color', '#1ea6ec');
    }
    if (value === 'dark-2') {
      localStorage.setItem('layout_version', 'dark-only');
      localStorage.setItem('primary_color', '#0288d1');
      localStorage.setItem('secondary_color', '#26c6da');
      localStorage.setItem('color', 'color-2');
    }
    if (value === 'dark-3') {
      localStorage.setItem('layout_version', 'dark-only');
      localStorage.setItem('primary_color', '#8e24aa');
      localStorage.setItem('secondary_color', '#ff6e40');
      localStorage.setItem('color', 'color-3');
    }
    if (value === 'dark-4') {
      localStorage.setItem('layout_version', 'dark-only');
      localStorage.setItem('primary_color', '#4c2fbf');
      localStorage.setItem('secondary_color', '#2e9de4');
      localStorage.setItem('color', 'color-4');
    }
    if (value === 'dark-5') {
      localStorage.setItem('layout_version', 'dark-only');
      localStorage.setItem('primary_color', '#7c4dff');
      localStorage.setItem('secondary_color', '#7b1fa2');
      localStorage.setItem('color', 'color-5');
    }
    if (value === 'dark-6') {
      localStorage.setItem('layout_version', 'dark-only');
      localStorage.setItem('primary_color', '#3949ab');
      localStorage.setItem('secondary_color', '#4fc3f7');
      localStorage.setItem('color', 'color-6');
    }
    window.location.reload();
  };

  return (
    <Fragment>
      <div className="customizer-links">
        <div
          className="nav flex-column nac-pills"
          id="c-pills-tab"
          role="tablist"
          aria-orientation="vertical"
        >
          <Nav tabs className="tab-list-bottom border-tab-primary">
            <NavItem className="nav nav-tabs" id="myTab" role="tablist">
              {/* eslint-disable-next-line */}
              <NavLink
                className={activeTab1 == '1' ? 'active' : ''}
                onClick={() => setActiveTab1('1')}
              >
                <div className="settings">
                  <i
                    className="icofont icofont-ui-settings"
                    onClick={openCustomizer}
                  ></i>
                </div>
              </NavLink>
            </NavItem>
            <NavItem className="nav nav-tabs" id="myTab" role="tablist">
              {/* eslint-disable-next-line */}
              <NavLink
                className={activeTab1 == '2' ? 'active' : ''}
                onClick={() => setActiveTab1('2')}
              >
                <div className="settings color-settings">
                  <i
                    className="icofont icofont-color-bucket"
                    onClick={openCustomizer}
                  ></i>
                </div>
              </NavLink>
            </NavItem>
          </Nav>
        </div>
      </div>
      <div className="customizer-contain">
        <div className="tab-content" id="c-pills-tabContent">
          <div className="customizer-header">
            <i className="icon-close" onClick={closeCustomizer}></i>
            <h5>Customizer</h5>
            <p className="mb-0">Customize &amp; Preview Real Time</p>
            <button
              className="btn btn-primary plus-popup mt-2 "
              onClick={() => setModal(!modal)}
            >
              Configuration
            </button>
            <Modal
              isOpen={modal}
              toggle={toggle}
              className="modal-body"
              centered={true}
            >
              <ModalHeader toggle={toggle}>Modal title</ModalHeader>
              <ModalBody>
                <div className="container-fluid bd-example-row">
                  <div className="row">
                    <p>
                      To replace our design with your desired theme. Please do
                      configuration as mention{' '}
                    </p>
                    <p>
                      {' '}
                      <b> Path : data > customizer > config.js </b>{' '}
                    </p>
                  </div>
                  <pre>
                    <code>
                      <div> export class ConfigDB &#123;</div>
                      <div> static data = &#123;</div>
                      <div> settings&#58; &#123;</div>
                      <div>
                        {' '}
                        layout_type&#58; : '{configDB.settings.layout_type}',
                      </div>

                      <div> sidebar&#58; &#123;</div>
                      <div>
                        {' '}
                        type&#58; : '{configDB.settings.sidebar.wrapper}'
                      </div>
                      <div>
                        {' '}
                        body_type&#58; : '
                        {configDB.settings.sidebar.bodyWrapper}'{' '}
                      </div>
                      <div> &#125;,</div>
                      <div>
                        {' '}
                        sidebar_setting&#58; : '
                        {configDB.settings.sidebar_setting}'{' '}
                      </div>
                      <div>
                        {' '}
                        sidebar_backround&#58; : '
                        {configDB.settings.sidebar_backround}'{' '}
                      </div>
                      <div> &#125;,</div>
                      <div> color&#58; &#123;</div>
                      <div>
                        {' '}
                        layout_version&#58; : '{
                          configDB.color.layout_version
                        }'{' '}
                      </div>
                      <div> color&#58; '{configDB.color.color}' </div>
                      <div>
                        {' '}
                        primary_color&#58; : '{
                          configDB.color.primary_color
                        }'{' '}
                      </div>
                      <div>
                        {' '}
                        secondary_color&#58; : '{configDB.color.secondary_color}
                        '{' '}
                      </div>
                      <div>
                        {' '}
                        mix_layout&#58; : '{configDB.color.mix_layout}'{' '}
                      </div>
                      <div> &#125;,</div>
                      <div> router_animation&#58; 'fadeIn'</div>
                      <div> &#125;</div>
                      <div> &#125;</div>
                    </code>
                  </pre>
                </div>
              </ModalBody>
              <ModalFooter>
                <CopyToClipboard text={JSON.stringify(configDB)}>
                  <button
                    className="btn btn-primary notification"
                    onClick={() =>
                      toast.success('Code Copied to clipboard !', {
                        position: toast.POSITION.BOTTOM_RIGHT,
                      })
                    }
                  >
                    Copy text
                  </button>
                </CopyToClipboard>
                <Button color="secondary" onClick={toggle}>
                  Cancel
                </Button>
              </ModalFooter>
            </Modal>
          </div>
          <div className="customizer-body custom-scrollbar">
            <TabContent activeTab={activeTab1}>
              <TabPane tabId="1">
                <h6>Layout Type</h6>
                <ul className="main-layout layout-grid">
                  <li
                    data-attr="ltr"
                    className={`${layout_type === 'ltr' ? 'active' : ''}`}
                    onClick={() => handleLayout('ltr')}
                  >
                    <div className="header bg-light">
                      <ul>
                        <li></li>
                        <li></li>
                        <li></li>
                      </ul>
                    </div>
                    <div className="body">
                      <ul>
                        <li className="bg-dark sidebar"></li>
                        <li className="bg-light body">
                          <span className="badge badge-dark">LTR Layout</span>
                        </li>
                      </ul>
                    </div>
                  </li>
                  <li
                    data-attr="rtl"
                    className={`${layout_type === 'rtl' ? 'active' : ''}`}
                    onClick={() => handleLayout('rtl')}
                  >
                    <div className="header bg-light">
                      <ul>
                        <li></li>
                        <li></li>
                        <li></li>
                      </ul>
                    </div>
                    <div className="body">
                      <ul>
                        <li className="bg-light body">
                          <span className="badge badge-dark">RTL Layout</span>
                        </li>
                        <li className="bg-dark sidebar"></li>
                      </ul>
                    </div>
                  </li>
                </ul>
                <h6 className="">Sidebar Type</h6>
                <ul className="sidebar-type layout-grid">
                  <li
                    data-attr="normal-sidebar"
                    className="active"
                    onClick={(e) => handleSidebarType(e, 'default', 'default')}
                  >
                    <div className="header bg-light">
                      <ul>
                        <li></li>
                        <li></li>
                        <li></li>
                      </ul>
                    </div>
                    <div className="body">
                      <ul>
                        <li className="bg-dark sidebar"></li>
                        <li className="bg-light body"></li>
                      </ul>
                    </div>
                  </li>
                  <li
                    data-attr="compact-sidebar"
                    onClick={(e) =>
                      handleSidebarType(e, 'compact-wrapper', 'sidebar-icon')
                    }
                  >
                    <div className="header bg-light">
                      <ul>
                        <li></li>
                        <li></li>
                        <li></li>
                      </ul>
                    </div>
                    <div className="body">
                      <ul>
                        <li className="bg-dark sidebar compact"></li>
                        <li className="bg-light body"></li>
                      </ul>
                    </div>
                  </li>
                  <li
                    data-attr="compact-icon-sidebar"
                    onClick={(e) =>
                      handleSidebarType(e, 'compact-page', 'sidebar-hover')
                    }
                  >
                    <div className="header bg-light">
                      <ul>
                        <li></li>
                        <li></li>
                        <li></li>
                      </ul>
                    </div>
                    <div className="body">
                      <ul>
                        <li className="bg-dark sidebar compact-icon"></li>
                        <li className="bg-light body"> </li>
                      </ul>
                    </div>
                  </li>
                  <li
                    data-attr="horizontal_sidebar"
                    className="horizontal_sidebar"
                    onClick={(e) =>
                      handleSidebarType(e, 'horizontal_sidebar', '')
                    }
                  >
                    <div className="header bg-light">
                      <ul>
                        <li></li>
                        <li></li>
                        <li></li>
                      </ul>
                    </div>
                    <div className="body">
                      <ul>
                        <li className="bg-dark sidebar horizontal-menu"></li>
                        <li className="bg-light body"> </li>
                      </ul>
                    </div>
                  </li>
                </ul>
                <h6 className="">Sidebar settings</h6>
                <ul className="sidebar-setting layout-grid">
                  <li
                    className="active"
                    data-attr="default-sidebar"
                    onClick={handleSidebarSetting}
                  >
                    <div className="header bg-light">
                      <ul>
                        <li></li>
                        <li></li>
                        <li></li>
                      </ul>
                    </div>
                    <div className="body bg-light">
                      <span className="badge badge-dark">Default</span>
                    </div>
                  </li>
                  <li data-attr="border-sidebar" onClick={handleSidebarSetting}>
                    <div className="header bg-light">
                      <ul>
                        <li></li>
                        <li></li>
                        <li></li>
                      </ul>
                    </div>
                    <div className="body bg-light">
                      <span className="badge badge-dark">Border</span>
                    </div>
                  </li>
                  <li
                    data-attr="iconcolor-sidebar"
                    onClick={handleSidebarSetting}
                  >
                    <div className="header bg-light">
                      <ul>
                        <li></li>
                        <li></li>
                        <li></li>
                      </ul>
                    </div>
                    <div className="body bg-light">
                      <span className="badge badge-dark">icon Color</span>
                    </div>
                  </li>
                </ul>
              </TabPane>
              <TabPane tabId="2">
                <h6>Light layout</h6>
                <ul className="layout-grid customizer-color">
                  <li
                    className="color-layout "
                    data-attr="light-1"
                    data-primary="#4466f2"
                    data-secondary="#1ea6ec"
                    onClick={() => colorChangeTheme('light-1')}
                  >
                    {' '}
                    <div></div>
                  </li>
                  <li
                    className="color-layout "
                    data-attr="light-2"
                    data-primary="#0288d1"
                    data-secondary="#26c6da"
                    onClick={() => colorChangeTheme('light-2')}
                  >
                    {' '}
                    <div></div>
                  </li>
                  <li
                    className="color-layout"
                    data-attr="light-3"
                    data-primary="#8e24aa"
                    data-secondary="#ff6e40"
                    onClick={() => colorChangeTheme('light-3')}
                  >
                    {' '}
                    <div></div>
                  </li>
                  <li
                    className="color-layout"
                    data-attr="light-4"
                    data-primary="#4c2fbf"
                    data-secondary="#2e9de4"
                    onClick={() => colorChangeTheme('light-4')}
                  >
                    {' '}
                    <div></div>
                  </li>
                  <li
                    className="color-layout"
                    data-attr="light-5"
                    data-primary="#7c4dff"
                    data-secondary="#7b1fa2"
                    onClick={() => colorChangeTheme('light-5')}
                  >
                    {' '}
                    <div></div>
                  </li>
                  <li
                    className="color-layout"
                    data-attr="light-6"
                    data-primary="#3949ab"
                    data-secondary="#4fc3f7"
                    onClick={() => colorChangeTheme('light-6')}
                  >
                    {' '}
                    <div></div>
                  </li>
                </ul>
                <h6 className="">Dark Layout</h6>
                <ul className="layout-grid customizer-color dark">
                  <li
                    className="color-layout"
                    data-attr="dark-1"
                    data-primary="#4466f2"
                    data-secondary="#1ea6ec"
                    onClick={() => colorChangeTheme('dark-1')}
                  >
                    {' '}
                    <div></div>
                  </li>
                  <li
                    className="color-layout"
                    data-attr="dark-2"
                    data-primary="#0288d1"
                    data-secondary="#26c6da"
                    onClick={() => colorChangeTheme('dark-2')}
                  >
                    {' '}
                    <div></div>
                  </li>
                  <li
                    className="color-layout"
                    data-attr="dark-3"
                    data-primary="#8e24aa"
                    data-secondary="#ff6e40"
                    onClick={() => colorChangeTheme('dark-3')}
                  >
                    {' '}
                    <div></div>
                  </li>
                  <li
                    className="color-layout"
                    data-attr="dark-4"
                    data-primary="#4c2fbf"
                    data-secondary="#2e9de4"
                    onClick={() => colorChangeTheme('dark-4')}
                  >
                    {' '}
                    <div></div>
                  </li>
                  <li
                    className="color-layout"
                    data-attr="dark-5"
                    data-primary="#7c4dff"
                    data-secondary="#7b1fa2"
                    onClick={() => colorChangeTheme('dark-5')}
                  >
                    {' '}
                    <div></div>
                  </li>
                  <li
                    className="color-layout"
                    data-attr="dark-6"
                    data-primary="#3949ab"
                    data-secondary="#4fc3f7"
                    onClick={() => colorChangeTheme('dark-6')}
                  >
                    {' '}
                    <div></div>
                  </li>
                </ul>
                <h6 className="">Mix Layout</h6>
                <ul className="layout-grid customizer-mix">
                  <li
                    className="color-layout"
                    data-attr="light-only"
                    onClick={handleCustomizerMix}
                  >
                    <div className="header bg-light">
                      <ul>
                        <li></li>
                        <li></li>
                        <li></li>
                      </ul>
                    </div>
                    <div className="body">
                      <ul>
                        <li className="bg-light sidebar"></li>
                        <li className="bg-light body"> </li>
                      </ul>
                    </div>
                  </li>
                  <li
                    className="color-layout active"
