import React, { useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import '../assets/css/layout.css';
import { Nav, TabContent } from 'react-bootstrap';
import Data from '../Data';
const dummyData = [
  {
    id: '1',
    facility: '1',
    visitor: '2020.05.14',
    sortation: '이미지',
    phone: '010-****-***9',
    date: '2020.05.14',
    Validity: '30분',
    Approval: '승인',
  },
  {
    id: '2',
    facility: '99',
    visitor: '2020.05.14',
    sortation: '이미지',
    phone: '010-****-***1',
    date: '2020.08.14',
    Validity: '35분',
    Approval: '거절',
  },
  {
    id: '3',
    facility: '80',
    visitor: '2020.05.14',
    sortation: '이미지',
    phone: '010-****-***3',
    date: '2020.07.14',
    Validity: '30분',
    Approval: '승인',
  },
];
const Guidelist = () => {
  const history = useHistory();
  const [en, seten] = useState(false);
  const [zn, setzn] = useState(false);
  const 영어관리스위치 = () => {
    seten(!en);
  };
  let [누른탭, 누른탭변경] = useState(0);
  return (
    <>
      <div className="card-block row">
        <div className="col-sm-12 col-lg-12 col-xl-12" id="bennerH">
          <div className="bennerContent">
            <div className="benner">가이드관리</div>
            <div className="benner">
              <div className="benner-btn">
                <button
                  id="bnr-btn"
                  className="btn btn-danger btn-sm"
                  onClick={() => {
                    history.push('/recommendguide');
                  }}
                >
                  상품연결
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="card">
        <div className="card-header">
          <div className="lang-box">
            <div
              className="en-btn"
              onClick={() => {
                누른탭변경(0);
              }}
              style={{ color: 누른탭 === 0 ? 'red' : 'black' }}
            >
              영문관리
            </div>
            <div
              className="zn-btn"
              onClick={() => {
                누른탭변경(1);
              }}
              style={{ color: 누른탭 === 1 ? 'blue' : 'black' }}
            >
              중문관리
            </div>
          </div>
          <TabContent2 누른탭={누른탭} />
        </div>
      </div>

      {/* <div className="card-block row">
        <div className="col-sm-12 col-lg-12 col-xl-12"></div>
      </div> */}
    </>
  );
};
function TabContent2(props) {
  if (props.누른탭 === 0) {
    return (
      <div>
        <RecomendItemEn />
      </div>
    );
  } else if (props.누른탭 === 1) {
    return (
      <div>
        <RecomendItemZn />
      </div>
    );
  } else if (props.누른탭 === 2) {
    return <div>내용2</div>;
  }
}

const RecomendItemEn = () => {
  const [seachtext, setserchText] = React.useState('');
  const [dummyData, setDummyData] = React.useState(Data);
  const SerchFilter = () => {
    const filter = Data.filter((d) => d.nicname.indexOf(seachtext) > -1);
    setDummyData(filter);
  };
  const onRemove = (id) => {
    setDummyData(dummyData.filter((user) => user.id !== id));
  };
  const history = useHistory();
  const { id } = useParams();

  return (
    <div>
      <div className="card">
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="user-search">
              <div>
                Seach:
                <input
                  className="search-input"
                  placeholder="닉네임으로 검색하세요"
                  onChange={(e) => setserchText(e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                      SerchFilter();
                    }
                  }}
                />
              </div>
            </div>
            <div className="table-responsive">
              <table className="table">
                <thead className="thead-light">
                  <tr>
                    <th scope="col"></th>
                    <th scope="col">가입일시</th>
                    <th scope="col">소셜구분</th>
                    <th scope="col">언어</th>
                    <th scope="col">이메일</th>
                    <th scope="col">닉네임</th>
                    <th scope="col"></th>
                  </tr>
                </thead>
                <tbody>
                  {dummyData.map((a) => {
                    return (
                      <tr>
                        <th scope="row" key={a.id}>
                          {a.id}
                        </th>
                        <td>{a.datea}</td>
                        <td>{a.sns}</td>
                        <td>{a.lang}</td>
                        <td>{a.email}</td>
                        <td>{a.nicname}</td>

                        <td>
                          <button
                            className="user-view-button"
                            onClick={() => {
                              history.push(`/userdetails/${a.id}`);
                            }}
                          >
                            보기
                          </button>
                          {/* <button
                            className="user-view-button"
                            onClick={() => onRemove(a.id)}
                          >
                            삭제
                          </button> */}
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
const RecomendItemZn = () => {
  return (
    <div className="table-responsive">
      <table className="table">
        <thead className="thead-light">
          <tr>
            <th scope="col"></th>
