import React, { useState } from 'react';
import { keyframes } from 'styled-components';
import '../assets/css/layout.css';
import { Link } from 'react-router-dom';
import filtersReducer from '../reducers/filters.reducer';
const Hashtag = () => {
  const [en, seten] = useState(false);
  const [zn, setzn] = useState(false);
  const 영어관리스위치 = () => {
    seten(!en);
  };
  const 내가원하는 = () => {};

  return (
    <div>
      <div className="col-sm-12">
        <div className="card">
          <div className="card-header">
            <h5>해시태그 관리</h5>
          </div>
          <div className="card-body">
            <div className="lang-box">
              <div onClick={영어관리스위치}>영문관리</div>
              <div>{영문관리스위치}중문관리</div>
            </div>

            {en === true && <Enup />}
            {en !== true && <Znup />}
          </div>
        </div>
      </div>
    </div>
  );
};
const Znup = () => {
  const [teg, settag] = useState([]);
  const [usertext, setusertxt] = useState('');

  const addClick = () => {
    const arrayCopy = teg.concat(usertext);
    // arrayCopy;
    settag(arrayCopy);
    setusertxt('');
  };

  return (
    <div>
      <div class="container">
        <div className="row">
          {teg.map((d, i) => (
            <div className="tag-box" key={i}>
              <p>
                {' '}
                <button
                  className="close"
                  onClick={() => {
                    '';
                  }}
                >
                  <span aria-hidden="true">&times;</span>
                </button>
                <div className="tag-item">{d}</div>
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};
const Enup = () => {
  const [teg, settag] = useState([]);
  const [usertext, setusertxt] = useState('');

  const addClick = () => {
    const arrayCopy = teg.concat(usertext);
    // arrayCopy;
    settag(arrayCopy);
    setusertxt('');
  };

  return (
    <>
      <div>
        <label>
          해시태그추가 &nbsp;
          {/* {alert('한글자 이상 입력해주시기바랍니다')} */}
          <input
            maxLength="5"
            placeholder="중문으로입력하세요"
            onChange={(e) => {
              setusertxt(e.target.value);
            }}
            value={usertext}
            onKeyDown={(e) => {
              if (e.key === 'Enter') {
                addClick();
              }
            }}
          />
        </label>{' '}
        &nbsp;
        <button className="btn btn-primary btn-xs" onClick={addClick}>
          추가
        </button>
      </div>

      <div class="container">
        <div className="row">
          {teg.map((d, i) => (
            <div className="tag-box" key={i}>
              <p>
                {' '}
                <button
                  className="close"
                  onClick={() => {
                    '';
                  }}
                >
                  <span aria-hidden="true">&times;</span>
                </button>
                <div className="tag-item">{d}</div>
              </p>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};
export default Hashtag;
