import React, { useContext, useState, useEffect, isValidElement } from "react"
import { useHistory } from "react-router-dom"
import "../assets/css/layout.css"
import NewCard from "../components/common/NewCard"
import PagnationTool from "../PagnationTool"
import Lightbox from "react-image-lightbox"
import "react-image-lightbox/style.css"
import Cookie from "js-cookie"
import axios from "axios"
import Pagenation from "../util/Pagenation"

const BannerList = () => {
  const history = useHistory()
  const [isOpen, setIsOpen] = useState(false)
  const [photoIndex, setPhotoIndex] = useState(0)
  const [users, setUsers] = useState(null)
  const [bannerlists, setBannerlists] = useState({})

  const deleteList = (listNo, index) => {
    if (window.confirm("삭제 하시겠습니까?")) {
      let form = new FormData()
      form.append("notice_no", listNo)
      axios
        .post("/api/v1/notice/deleteU", form, {
          headers: { Authorization: Cookie.get("token") }
        })
        .then(response => {
          const list = bannerlists.filter((item, i) => i !== index)
          console.log(list)
          setBannerlists(list)
        })
    }
  }

  useEffect(() => {
    let form = new FormData()
    form.append("index", 1)
    axios
      .post("/api/v1/banner/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(function(response) {
        console.log(response)
        if (response.data.status == "success") {
          setBannerlists({
            lists: response.data.data.list,
            count: response.data.data.count
          })
        }
      })
  }, [])

  const fn = async d => {
    let form = new FormData()
    form.append("index", d)

    try {
      const { data } = await axios.post("/api/v1/banner/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })

      if (data.status == "success") {
        setBannerlists({
          lists: data.data.list,
          count: data.data.count
        })
      }
    } catch (error) {
      alert("페이지를 로딩하는데 실패했어요.")
    }

    // 이전 다음 페이지를 위한 api호출
    // axios로 서버 통신
    // success시 nomalList 업데이트
  }

  return (
    <>
      <NewCard title="배너관리" parent="배너 리스트">
        <div className="bennerContent">
          <h5>배너관리</h5>
          <button
            className="btn btn-danger btn-sm"
            onClick={() => {
              history.push("/banneradd/")
            }}
          >
            배너등록
          </button>
        </div>
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="table-responsive">
              <table className="table">
                <thead className="thead-light">
                  <tr>
                    <th scope="col" />
                    <th scope="col">노출순위</th>
                    <th scope="col">작성일시</th>
                    <th scope="col">대표이미지</th>
                    <th scope="col" />
                  </tr>
                </thead>
                <tbody>
                  {bannerlists.lists &&
                    bannerlists.lists.map((list, index) => {
                      return (
                        <tr>
                          <th scope="row" key={list.banner_no}>
                            {list.banner_no}
                          </th>
                          <td>{list.z_index}</td>
                          {/* <td>{list.username}</td> */}
                          <td>{list.registered}</td>
                          <td>
                            <img
                              src={list.zh_image}
                              alt="Gallery"
                              className="img-100 b-r-15"
                              onClick={() => {
                                setIsOpen(true)
                                setPhotoIndex(1)
                              }}
                            />
                            &nbsp;&nbsp;
                            <img
                              src={list.en_image}
                              alt="Gallery"
                              className="img-100 b-r-15"
                              onClick={() => {
                                setIsOpen(true)
                                setPhotoIndex(1)
                              }}
                            />
                          </td>
                          <td>
                            <button
                              className="btn btn-warning active  btn -xs btn-sm"
                              type="button"
                              onClick={() => deleteList(list.banner_no, index)}
                            >
                              삭제
                            </button>
                            &nbsp; &nbsp;
                            {/* <button className="edit-button">수정</button>{' '} */}
                            <button
                              className="btn btn-secondary btn-sm"
                              style={{ backgroundColor: "#43a1df" }}
                              type="button"
                              onClick={() => {
                                history.push(`/banner/update/${list.banner_no}`)
                              }}
                            >
                              수정
                            </button>
                          </td>
                        </tr>
                      )
                    })}
                </tbody>
              </table>
            </div>
          </div>
        </div>
        <div className="pagenation-box">
          <Pagenation data={bannerlists} fn={fn} />
        </div>
      </NewCard>
    </>
  )
}

export default BannerList
