import React, { useState } from "react"
import { useHistory, useParams } from "react-router-dom"
import "../assets/css/layout.css"
import { Nav, TabContent } from "react-bootstrap"
import Data from "../Data"
import Breadcrumb from "../components/common/breadcrumb"
import PagnationTool from "../PagnationTool"
import one from "../assets/images/product/1.png"

const GorpGoodsList = () => {
  const history = useHistory()
  let [tab, settab] = useState(0)
  // const [en, seten] = useState(false);
  // const [zn, setzn] = useState(false);
  // // const 영어관리스위치 = () => {
  //   seten(!en);
  // };
  return (
    <>
      <Breadcrumb title="단체투어리스트" parent="단체투어상품관리" />
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm-12">
            <div className="card">
              <div className="card-header">
                <div className="card-body">
                  <div className="nav-sub">
                    <Nav
                      variant="tabs"
                      defaultActiveKey="link-0"
                      className="nav--sub2"
                    >
                      <div className="navtab">
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-0"
                            onClick={() => {
                              settab(0)
                            }}
                          >
                            승인 상태
                          </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-1"
                            onClick={() => {
                              settab(1)
                            }}
                          >
                            판매중
                          </Nav.Link>
                        </Nav.Item>
                      </div>
                      <div>
                        {/* <button
                          id="maching-btn"
                          className="btn btn-danger btn-sm"
                          onClick={() => {
                            history.push('/recomendguidecnt');
                          }}
                        >
                          상품연결
                        </button> */}
                      </div>
                    </Nav>
                    <TabContent2 tab={tab} />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* <div className="card-block row">
        <div className="col-sm-12 col-lg-12 col-xl-12"></div>
      </div> */}
    </>
  )
}
function TabContent2(props) {
  if (props.tab === 0) {
    return (
      <div>
        <GuideSome />
      </div>
    )
  } else if (props.tab === 1) {
    return (
      <div>
        <GuideOk />
      </div>
    )
  }
}

const GuideSome = () => {
  const [seachtext, setserchText] = React.useState("")
  const [dummyData, setDummyData] = React.useState(Data)
  const SerchFilter = () => {
    const filter = Data.filter(d => d.name.indexOf(seachtext) > -1)
    setDummyData(filter)
  }
  const onRemove = id => {
    setDummyData(dummyData.filter(user => user.id !== id))
  }
  const history = useHistory()
  const { id } = useParams()

