              <div className="table-responsive">
                <table className="table">
                  <thead className="thead-light">
                    <tr>
                      <th scope="col"></th>
                      <th scope="col">결제일시</th>
                      <th scope="col">상품명</th>
                      <th scope="col"></th>

                      <th scope="col">유저명</th>
                      <th scope="col">이용일자</th>
                      <th scope="col">결제금액</th>
                      <th scope="col"></th>
                    </tr>
                  </thead>
                  <tbody>
                    {dummyData.map((a) => {
                      return (
                        <tr>
                          <th scope="row" key={a.id}>
                            {a.id}
                          </th>
                          <td>{a.datea}</td>

                          <td>
                            {" "}
                            <img
                              src={one}
                              alt="Gallery"
                              className="img-60 b-r-15"
                            />
                          </td>
                          <td>
                            {a.product}/<br />
                            {a.product}
                          </td>

                          <td>{a.name}</td>
                          <td>{a.datea}</td>
                          <td>180,000원</td>

