                      <th scope="col">상품정보</th>
                      <th scope="col"></th>
                      <th scope="col"></th>
                      <th scope="col"></th>
                    </tr>
                  </thead>
                  <tbody>
                    {dummyData.map((a) => {
                      return (
                        <tr>
