import React, { useState, useRef, useCallback } from "react"
import "../assets/css/layout.css"
import Breadcrumb from "../components/common/breadcrumb"
import NewCard from "../components/common/NewCard"
import axios from "axios"
import Cookie from "js-cookie"
import { Editor } from "react-draft-wysiwyg"
import "./editer.css"
import { EditorState, convertToRaw } from "draft-js"
import draftToHtml from "draftjs-to-html"
import htmlToDraft from "html-to-draftjs"

const AddNotiGuide = ({ history }) => {
  const [KoTitle, setKoTitle] = useState("")
  const [KoContent, setKoContent] = useState("")

  // 에디터에 따라다르지만  상태값이름을 통일해야 돌아감
  const [editorState, setEditorState] = useState(EditorState.createEmpty())

  // const onKoContentHandler = e => {
  //   const content = e.editor.getData()

  //   setKoContent(content)
  // }

  const onUpload = files => {
    const form = new FormData()

    form.append("type", "A")
    form.append("file", files)

    return axios
      .post("/api/v1/file/uploadFile", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(res => {
        return { data: { link: res.data.data } }
      })
      .catch(err => {
        console.log(err)
      })
  }

  const onEditorStateChange = data => {
    setEditorState(data)
  }

  const onSubmithandler = async e => {
    e.preventDefault()

    const editorContent = draftToHtml(
      // 값을 변환해서 넘겨줘야 함

      convertToRaw(editorState.getCurrentContent())
    )

    // const Koeditor = koEditerContens.current.getInstance().getHtml()
    let form = new FormData()
    form.append("title", KoTitle)
    form.append("editor", editorContent)

    const cookie = Cookie.get("token")

    try {
      const { data } = await axios.post("/api/v1/notice/addG", form, {
        headers: {
          Authorization: cookie
        }
      })
      if (data.status == "success") {
        alert("성공적으로 작성되었습니다.")
        history.push("/notice/guide")
      }
    } catch (error) {
      alert("작성하는데 실패했습니다.")
    }
  }
  const KoText = () => {
    // console.log("????")
    return (
      <div className="col-sm-12">
        <div className="card">
          <div className="card-header">
            <h5>영문내용입력하기</h5>
          </div>
          <div className="card-body">
            {/* <Editor
              initialValue="hello react editor world!"
              previewStyle="vertical"
              height="400px"
              ref={koEditerContens}
              initialEditType="wysiwyg" */}
            // useCommandShortcut={true}
            />
          </div>
        </div>
      </div>
    )
  }

  return (
    <>
      {/* <Breadcrumb title="가이드용 공지사항" parent="공지사항관리" /> */}
      <NewCard title="가이드용 공지사항" parent="공지사항관리">
        <div className="noti-title">
          <form>
            <h5>제목입력</h5>
            <input
              onChange={e => setKoTitle(e.target.value)}
              value={KoTitle}
              class="form-control"
              id="exampleFormControlInput1"
              type="input"
              placeholder="한글로입력해주세요"
            />
          </form>
        </div>

        {/* <div className="col-sm-12"> */}
        <div className="eiter_size">
          <Editor
            editorState={editorState}
            toolbarClassName="toolbarClassName"
            wrapperClassName="home-wrapper"
            editorClassName="home-editor"
            onEditorStateChange={onEditorStateChange}
            toolbar={{
              // inline: { inDropdown: true },
              // list: { inDropdown: true },
              // textAlign: { inDropdown: true },
              // link: { inDropdown: true },
              // history: { inDropdown: true },
              image: { uploadCallback: onUpload }
            }}
            localization={{
              locale: "ko"
            }}
          />
        </div>

        <div className="col-sm-12">
          <div>
            <div>
              <button
                className="btn btn-square btn-warning btn-sm"
                type="button"
              >
                삭제
              </button>
              &nbsp; &nbsp;
              {/* <button className="edit-button">수정</button>{' '} */}
              <button
                className="btn btn-square btn-secondary active btn-sm"
                type="button"
                onClick={onSubmithandler}
              >
                작성
              </button>
            </div>
          </div>
        </div>
      </NewCard>
    </>
  )
}

export default AddNotiGuide
