import React, { useState } from 'react';

const EnTag = () => {
  const [teg, settag] = useState(['사관', '욕설', '금지', '태그']);
  const [usertext, setusertxt] = useState('');
  const addClick = () => {
    const arrayCopy = teg.concat(usertext);
    // arrayCopy;
    settag(arrayCopy);
    setusertxt('');
    return (
      <>
        <div>
          <label>
            해시태그추가 &nbsp;
            <input
              placeholder="영문으로입력하세요"
              onChange={(e) => {
                setusertxt(e.target.value);
              }}
              value={usertext}
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                  addClick();
                }
              }}
            />
          </label>{' '}
          &nbsp;
          <button className="btn btn-primary btn-xs" onClick={addClick}>
            추가
          </button>
        </div>

        <div class="container">
          <div className="row">
            {teg.map((d, i) => (
              <div className="tag-box" key={i}>
                <p>
                  {' '}
                  <button
                    className="close"
                    onClick={() => {
                      '';
                    }}
                  >
                    <span aria-hidden="true">&times;</span>
                  </button>
                  <div className="tag-item"> {d}</div>
                </p>
              </div>
            ))}
          </div>
        </div>
      </>
    );
  };
};
export default EnTag;
