import React, { useState } from 'react';

const Hashtag = () => {
  const [태그, 태그변경] = useState(['사관']);
  const [입력값, 입력값변경] = useState('');
  return (
    <div>
      <div class="container">
        <div class="row">
          <div class="col-md-7">
            <h4>검색 해시태그 관리 </h4>
            <div>영문관리</div>
            <div>중문관리</div>
            <div>해시태그추가</div>
            <from>
              <input
                onChange={(e) => {
                  입력값변경(e.target.value);
                }}
              />
            </from>
            <button
              onclick={() => {
                const arrayCopy = [...태그];
                arrayCopy.unshift(입력값);
                태그변경(arrayCopy);
              }}
            >
              {' '}
              추가
            </button>
          </div>
        </div>
      </div>
      <div>
        {태그.map((d) => (
          <h3>{d}</h3>
        ))}
      </div>
    </div>
  );
};

export default Hashtag;
