import React, { useState } from 'react';
import { keyframes } from 'styled-components';

const Hashtag = () => {
  const [teg, settag] = useState(['사관', '욕설', '금지', '태그']);
  const [usertext, setusertxt] = useState('');
  const addClick = () => {
    const arrayCopy = teg.concat(usertext);
    // arrayCopy;
    settag(arrayCopy);
    setusertxt('');
  };

  return (
    <div>
      <div class="container">
        <div class="row">
          <div class="col-md-7">
            <h4>검색 해시태그 관리 </h4>
            <div>영문관리</div>
            <div>중문관리</div>
            <div>해시태그추가</div>

            <input
              onChange={(e) => {
                setusertxt(e.target.value);
              }}
              value={usertext}
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                }
              }}
            />

            <button
              onClick={
                addClick
                //   () => {
                //   const arrayCopy = teg.concat(usertext);
                //   // arrayCopy;
                //   settag(arrayCopy);
                //   setusertxt('');
                // }
              }
            >
              추가
            </button>
          </div>
        </div>
      </div>
      <div>
        {teg.map((d, i) => (
          <h3 key={i}>{d}</h3>
        ))}
      </div>
    </div>
  );
};

export default Hashtag;
