import React, { useState, useCallback, useRef } from "react"
import "../assets/css/layout.css"

import Breadcrumb from "../components/common/breadcrumb"
import axios from "axios"
import Editor from "../toastEditor/ToastEditor"
import Cookie from "js-cookie"
const AddNotiUser = ({ history }) => {
  // const [inputs, setInputs] = useState({
  //   zh_title: "",
  //   en_title: "",
  //   zn_content: "",
  //   en_content: "",
  // })

  const [ZhTitle, setZhTitle] = useState("")
  const [EnTitle, setEnTitle] = useState("")
  const [Zheditor, setZheditor] = useState("")
  const [Eneditor, setEneditor] = useState("")

  const enEditorRef = useRef()
  const zhEditorRef = useRef()

  // 에디터에 참조값

  const onChangeHandler = () => {
    const content = editorRef.current.getInstance().getHtml()
  }

  const imageHandler = async (file, callback) => {
    // 파일 업로드
    const form = new FormData()
    form.append("type", "A")
    form.append("file", file)

    const { data } = await axios.post("/api/v1/File/uploadFile", form, {
      headers: {
        Authorization: Cookie.get("token")
      }
    })

    callback(data.data)
  }

  const onSubmithandler = async e => {
    e.preventDefault()

    let form = new FormData()

    form.append("zh_editor", Zheditor)
    form.append("en_editor", Eneditor)
    form.append("zh_title", ZhTitle)
    form.append("en_title", EnTitle)
    const cookie = Cookie.get("token")
    try {
      const { data } = await axios.post("/api/v1/notice/addU", form, {
        headers: {
          Authorization: cookie
        }
      })
      if (data.status === "success") {
        alert("성공적으로 작성되었습니다.")
        history.push("/notice")
      }
    } catch (error) {
      alert("작성하는데 실패했습니다.")
    }
  }

  return (
    <>
      <Breadcrumb title="유저용공지사항 작성" parent="공지사항관리" />
      <div className="card">
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="card-body">
              <div className="noti-title">
                <form>
                  <div className="card-header">
                    <h5>제목입력</h5> 
                  </div>
                  <input
                    onChange={e => setEnTitle(e.target.value)}
                    value={EnTitle}
                    class="form-control"
                    id="exampleFormControlInput1"
                    type="input"
                    placeholder="영문으로입력하세요"
                  />
                  <p />
                  <input
                    onChange={e => setZhTitle(e.target.value)}
                    value={ZhTitle}
                    class="form-control"
                    id="exampleFormControlInput1"
                    type="input"
                    placeholder="중문으로입력하세요"
                  />
                </form>
              </div>
            </div>

            <div className="col-sm-12" style={{ marginBottom: "20px" }}>
              <div className="card-header">
                <h5>영문 내용 입력하기</h5> 
              </div>
              <Editor editorContent={editorContent} value={true} />
            </div>
            <div className="col-sm-12">
              <div className="card-header">
                <h5>중문내용 입력하기</h5> 
              </div>
              <Editor editorContent={editorContent} />
              <div>
                {/* <CkEditerZn onChange={onEnContentHandler} /> */}
                {}
                <div>
                  <button
                    className="btn btn-square btn-warning btn-sm"
                    type="button"
                  >
                    삭제
                  </button>
                  &nbsp; &nbsp;
                  {/* <button className="edit-button">수정</button>{' '} */}
                  <button
                    className="btn btn-square btn-secondary active btn-sm"
                    onClick={onSubmithandler}
                  >
                    작성
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default AddNotiUser
