import React, { useState } from 'react';
import { keyframes } from 'styled-components';
import '../assets/css/layout.css';
import { Link } from 'react-router-dom';
import filtersReducer from '../reducers/filters.reducer';
import { TabContent } from 'reactstrap';
const Hashtag = () => {
  const [en, seten] = useState(0);

  const 내가원하는 = () => {};

  return (
    <div>
      <div className="col-sm-12">
        <div className="card">
          <div className="card-header">
            <h5>해시태그 관리</h5>
          </div>
          <div className="card-body">
            <div className="lang-box">
              <div
                className="en-btn"
                onClick={() => {
                  seten(0);
                }}
                style={{ color: en === 0 ? 'red' : 'black' }}
              >
                영문관리
              </div>
              <div
                className="zn-btn"
                onClick={() => {
                  seten(1);
                }}
                style={{ color: en === 1 ? 'black' : 'bule' }}
              >
                > 중문관리
              </div>
            </div>

            <TabContentTab en={en} />
          </div>
        </div>
      </div>
    </div>
  );
};
function TabContentTab(props) {
  if (props.en === 0) {
    return (
      <div>
        <Enup />
      </div>
    );
  } else if (props.en === 1) {
    return (
      <div>
        <Znup />
      </div>
    );
  } else if (props.누른탭 === 2) {
    return <div>내용2</div>;
  }
}
const Znup = () => {
  const [teg, settag] = useState([]);
  const [usertext, setusertxt] = useState('');

  const addClick = () => {
    const arrayCopy = teg.concat(usertext);
    // arrayCopy;
    settag(arrayCopy);
    setusertxt('');
  };

  return (
    <>
      <div>
        <label>
          해시태그추가 &nbsp;
          {/* {alert('한글자 이상 입력해주시기바랍니다')} */}
          <input
            maxLength="5"
            placeholder="중문입력"
            onChange={(e) => {
              setusertxt(e.target.value);
            }}
            value={usertext}
            onKeyDown={(e) => {
              if (e.key === 'Enter') {
                addClick();
              }
            }}
          />
        </label>{' '}
        &nbsp;
        <button className="btn btn-primary btn-xs" onClick={addClick}>
          추가
        </button>
      </div>

      <div class="container">
        <div className="row">
          {teg.map((d, i) => (
            <div className="tag-box" key={i}>
              <p>
                {' '}
                <button
                  className="close"
                  onClick={() => {
                    '';
                  }}
                >
                  <span aria-hidden="true">&times;</span>
                </button>
                <div className="tag-item">{d}</div>
              </p>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};
const Enup = () => {
  const [teg, settag] = useState([]);
  const [usertext, setusertxt] = useState('');

  const addClick = () => {
    const arrayCopy = teg.concat(usertext);
    // arrayCopy;
    settag(arrayCopy);
    setusertxt('');
  };

  return (
    <>
      <div>
        <label>
          해시태그추가 &nbsp;
          {/* {alert('한글자 이상 입력해주시기바랍니다')} */}
          <input
            maxLength="5"
            placeholder="영문입력"
            onChange={(e) => {
              setusertxt(e.target.value);
            }}
            value={usertext}
            onKeyDown={(e) => {
              if (e.key === 'Enter') {
                addClick();
              }
            }}
          />
        </label>{' '}
        &nbsp;
        <button className="btn btn-primary btn-xs" onClick={addClick}>
          추가
        </button>
      </div>

      <div class="container">
        <div className="row">
          {teg.map((d, i) => (
            <div className="tag-box" key={i}>
              <p>
                {' '}
                <button
                  className="close"
                  onClick={() => {
                    '';
                  }}
                >
                  <span aria-hidden="true">&times;</span>
                </button>
                <div className="tag-item">{d}</div>
              </p>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};
export default Hashtag;
