import React, { useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import '../assets/css/layout.css';
import { Nav, TabContent } from 'react-bootstrap';
import Data from '../Data';
import Breadcrumb from '../components/common/breadcrumb';

const Guidelist = () => {
  const history = useHistory();
  // const [en, seten] = useState(false);
  // const [zn, setzn] = useState(false);
  // // const 영어관리스위치 = () => {
  //   seten(!en);
  // };
  let [누른탭, 누른탭변경] = useState(0);
  return (
    <>
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm-12">
            <div className="card">
              <div className="card-header">
                <div className="bennerContent"></div>

                <div className="card-body">
                  <div className="nav-sub">
                    <Nav
                      variant="tabs"
                      defaultActiveKey="link-0"
                      className="nav--sub2"
                    >
                      <div className="navtab">
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-0"
                            onClick={() => {
                              누른탭변경(0);
                            }}
                          >
                            승인 상태
                          </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-1"
                            onClick={() => {
                              누른탭변경(1);
                            }}
                          >
                            활동중
                          </Nav.Link>
                        </Nav.Item>
                      </div>
                      <div>
                        {/* <button
                          id="maching-btn"
                          className="btn btn-danger btn-sm"
                          onClick={() => {
                            history.push('/recomendguidecnt');
                          }}
                        >
                          상품연결
                        </button> */}
                      </div>
                    </Nav>
                    <TabContent2 누른탭={누른탭} />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* <div className="card-block row">
        <div className="col-sm-12 col-lg-12 col-xl-12"></div>
      </div> */}
    </>
  );
};
function TabContent2(props) {
  if (props.누른탭 === 0) {
    return (
      <div>
        <GuideSome />
      </div>
    );
  } else if (props.누른탭 === 1) {
    return (
      <div>
        <GuideOk />
      </div>
    );
  }
}

const GuideSome = () => {
  const [seachtext, setserchText] = React.useState('');
  const [dummyData, setDummyData] = React.useState(Data);
  const SerchFilter = () => {
    const filter = Data.filter((d) => d.nicname.indexOf(seachtext) > -1);
    setDummyData(filter);
  };
  const onRemove = (id) => {
    setDummyData(dummyData.filter((user) => user.id !== id));
  };
  const history = useHistory();
  const { id } = useParams();

  return (
    <div>
      <div className="card">
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="card-body">
              <div className="user-search-bar">
                <div className="search-input">
                  <div className="form-group">
                    <input
                      className="form-control"
                      type="text"
                      placeholder="닉네임으로 검색하세요"
                      onChange={(e) => setserchText(e.target.value)}
                      onKeyDown={(e) => {
                        if (e.key === 'Enter') {
                          SerchFilter();
                        }
                      }}
                    />
                  </div>
                </div>
                <div onClick={SerchFilter} className="icon-search">
                  <i className="fa fa-search"></i>
                </div>
              </div>

              <div className="table-responsive">
                <table className="table">
                  <thead className="thead-light">
                    <tr>
                      <th scope="col"></th>
                      <th scope="col">승인상태</th>
                      <th scope="col">작성일시</th>
                      <th scope="col">가이드정보</th>
                      <th scope="col"></th>
                    </tr>
                  </thead>
                  <tbody>
                    {dummyData.map((a) => {
                      return (
                        <tr>
                          <th scope="row" key={a.id}>
                            {a.id}
                          </th>
                          <td>{a.Approval}</td>
                          <td>{a.datea}</td>
                          <td>
                            {a.name}/{a.age}/{a.lang}/{a.area}
                          </td>

                          <td>
                            <button
                              className=" btn btn-square btn-outline-light txt-dark btn-sm"
                              onClick={() => {
                                history.push(`/userdetails/${a.id}`);
                              }}
                            >
                              보기
                            </button>
                            {/* <button
