import React, { useContext, useState, useEffect, isValidElement } from "react"
import { useHistory } from "react-router-dom"
import "../assets/css/layout.css"
import NewCard from "../components/common/NewCard"
import PagnationTool from "../PagnationTool"
import Lightbox from "react-image-lightbox"
import "react-image-lightbox/style.css"
import Cookie from "js-cookie"
import axios from "axios"

const images = [
  require("../assets/images/big-lightgallry/013.jpg"),
  require("../assets/images/big-lightgallry/014.jpg"),
  require("../assets/images/big-lightgallry/015.jpg"),
  require("../assets/images/big-lightgallry/016.jpg"),
  require("../assets/images/big-lightgallry/01.jpg"),
  require("../assets/images/big-lightgallry/04.jpg"),
  require("../assets/images/big-lightgallry/05.jpg"),
  require("../assets/images/big-lightgallry/06.jpg"),
  require("../assets/images/big-lightgallry/07.jpg"),
  require("../assets/images/big-lightgallry/08.jpg"),
  require("../assets/images/big-lightgallry/07.jpg"),
  require("../assets/images/big-lightgallry/08.jpg")
]

const smallImages = [
  require("../assets/images/lightgallry/013.jpg"),
  require("../assets/images/lightgallry/015.jpg"),
  require("../assets/images/lightgallry/014.jpg"),
  require("../assets/images/lightgallry/016.jpg"),
  require("../assets/images/lightgallry/01.jpg"),
  require("../assets/images/lightgallry/04.jpg"),
  require("../assets/images/lightgallry/05.jpg"),
  require("../assets/images/lightgallry/06.jpg"),
  require("../assets/images/lightgallry/07.jpg"),
  require("../assets/images/lightgallry/08.jpg"),
  require("../assets/images/lightgallry/07.jpg"),
  require("../assets/images/lightgallry/08.jpg")
]

const BennerList = () => {
  const history = useHistory()
  const [isOpen, setIsOpen] = useState(false)
  const [photoIndex, setPhotoIndex] = useState(0)
  const [users, setUsers] = useState(null)
  const [lists, setlists] = useState([])

  const deleteList = (listNo, index) => {
    if (window.confirm("삭제 하시겠습니까?")) {
      console.log(listNo)
      let form = new FormData()
      form.append("notice_no", listNo)
      axios
        .post("/api/v1/notice/deleteU", form, {
          headers: { Authorization: Cookie.get("token") }
        })
        .then(response => {
          const list = lists.filter((item, i) => i !== index)
          console.log(list)
          setlists(list)
        })
    }
  }

  useEffect(() => {
    let form = new FormData()
    form.append("index", 1)
    axios
      .post("/api/v1/banner/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(function(response) {
        console.log(response)
        if (response.data.status == "success") {
          setlists(response.data.data.list)
        }
      })
  }, [])

  // let 데이터 = useContext(데이터context);
  return (
    <>
      <NewCard title="배너관리" parent="배너 리스트">
        <div className="bennerContent">
          <h5>배너관리</h5>
          <button
            className="btn btn-danger btn-sm"
            onClick={() => {
              history.push("/banneradd/")
            }}
          >
            배너등록
          </button>
        </div>
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="table-responsive">
              <table className="table">
                <thead className="thead-light">
                  <tr>
                    <th scope="col" />
                    <th scope="col">노출순위</th>
                    <th scope="col">작성일시</th>
                    <th scope="col">대표이미지</th>
                    <th scope="col" />
                  </tr>
                </thead>
                <tbody>
                  {lists.map((list, index) => {
                    return (
                      <tr>
                        <th scope="row" key={list.banner_no}>
                          {list.banner_no}
                        </th>
                        <td>{list.z_index}</td>
                        {/* <td>{list.username}</td> */}
                        <td>{list.registered}</td>
                        <td>
                          <img
                            src={list.zh_image}
                            alt="Gallery"
                            className="img-100 b-r-15"
                            onClick={() => {
                              setIsOpen(true)
                              setPhotoIndex(1)
                            }}
                          />
                          &nbsp;&nbsp;
                          <img
                            src={list.en_image}
                            alt="Gallery"
                            className="img-100 b-r-15"
                            onClick={() => {
                              setIsOpen(true)
                              setPhotoIndex(1)
                            }}
                          />
                        </td>
                        <td>
                          <button
                            className="btn btn-square btn-warning btn-sm"
                            type="button"
                            onClick={() => deleteList(list.banner_no, index)}
                          >
                            삭제
                          </button>
                          &nbsp; &nbsp;
                          {/* <button className="edit-button">수정</button>{' '} */}
                          <button
                            className="btn btn-square btn-secondary active btn-xs"
                            type="button"
                            onClick={() => {
                              history.push(`/banner/update/${list.banner_no}`)
                            }}
                          >
                            수정
                          </button>
                        </td>
                      </tr>
                    )
                  })}
                </tbody>
              </table>
            </div>
          </div>
        </div>
        <PagnationTool />
      </NewCard>
      {/* <Breadcrumb title="배너관리" parent="배너관리" />
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm-12">
            <div className="card">
              <div className="card-body">
                <div className="bennerContent">
                  <h5>배너관리</h5>
                  <button
                    id="bnr-btn"
                    className="btn btn-danger btn-sm"
                    onClick={() => {
                      history.push('/banneradd/');
                    }}
                  >
                    배너등록
                  </button>
                </div>
                <div className="card-block row">
                  <div className="col-sm-12 col-lg-12 col-xl-12">
                    <div className="table-responsive">
                      <table className="table">
                        <thead className="thead-light">
                          <tr>
                            <th scope="col"></th>
                            <th scope="col">노출순위</th>
                            <th scope="col">작성일시</th>
                            <th scope="col">대표이미지</th>
                            <th scope="col"></th>
                          </tr>
                        </thead>
                        <tbody>
                          {dummyData.map((a) => {
                            return (
                              <tr>
                                <th scope="row" key={a.id}>
                                  1
                                </th>
                                <td>{a.facility}</td>
                                <td>{a.date}</td>
                                <td>{a.sortation}</td>
                                <td>
                                  <button className="cancel-btn">취소</button>
                                  &nbsp; &nbsp;
                                  <button className="edit-button">
                                    수정
                                  </button>{' '}
                                </td>
                              </tr>
                            );
                          })}
                        </tbody>
                      </table>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div> */}
      {isOpen && (
        <Lightbox
          mainSrc={images[photoIndex]}
          nextSrc={images[(photoIndex + 1) % images.length]}
          prevSrc={images[(photoIndex + images.length - 1) % images.length]}
          imageTitle={photoIndex + 1 + "/" + images.length}
          onCloseRequest={() => setIsOpen(false)}
          onMovePrevRequest={() =>
            setPhotoIndex({
              photoIndex: (photoIndex + images.length - 1) % images.length
            })
          }
          onMoveNextRequest={() =>
            setPhotoIndex({
              photoIndex: (photoIndex + 1) % images.length
            })
          }
        />
      )}
    </>
  )
}

export default BennerList
