import React, { useState } from "react"
import "../assets/css/layout.css"
import { useHistory, useParams } from "react-router-dom"
import DropEn from "./DropEn"
import DropZn from "./DropZn"
import CkEditerEn from "./CkEditerEn"
import CkEditerZn from "./CkEditerZn"
import Breadcrumb from "../components/common/breadcrumb"
import axios from "axios"
import CKEditors from "react-ckeditor-component"
import Cookie from "js-cookie"

const AddBenner = () => {
  const [KoTitle, setKoTitle] = useState("")
  const [ZhContent, setZhContent] = useState("")
  const [EnContent, setEnContent] = useState("")
  const history = useHistory()
  const onZhContentHandler = (e) => {
    const content = e.editor.getData()

    setZhContent(content)
  }
  const onEnContentHandler = (e) => {
    const content = e.editor.getData()

    setEnContent(content)
  }
  const ZhText = () => {
    return (
      <div className="col-sm-12">
        <div className="card">
          <div className="card-header">
            <h5>영문내용입력하기</h5>
          </div>
          <div className="card-body">
            <CKEditors
              editor={CKEditors}
              content={ZhContent}
              events={{
                change: onZhContentHandler,
              }}
            />
          </div>
        </div>
      </div>
    )
  }
  const onSubmithandler = async (e) => {
    e.preventDefault()
    let form = new FormData()
    form.append("eh_editor", ZhContent)
    form.append("en_editor", EnContent)

    const cookie = Cookie.get("token")

    try {
      const { data } = await axios.post("/api/v1/banner/list", form, {
        headers: {
          Authorization: cookie,
        },
      })
      if (data.status == "success") {
        alert("성공적으로 작성되었습니다.")
        history.push("/bennerlist")
      }
    } catch (error) {
      alert("작성하는데 실패했습니다.")
    }
  }
  const EnText = () => {
    // console.log("????")
    return (
      <div className="col-sm-12">
        <div className="card">
          <div className="card-header">
            <h5>영문내용입력하기</h5>
          </div>
          <div className="card-body">
            <CKEditors
              editor={CKEditors}
              content={EnContent}
              events={{
                change: onEnContentHandler,
              }}
            />
          </div>
        </div>
      </div>
    )
  }

  return (
    <>
      <Breadcrumb title="배너관리" parent="배너추가" />
      <div className="card">
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="card-body">
              <form>
                {/* <div> */}
                <DropEn />
                <DropZn />
                <div>{EnText()}</div>
                <div>{ZhText()}</div>
                {/* </div> */}

                <div>
                  <button
                    className="btn btn-square btn-warning btn-sm"
                    type="button"
                  >
                    삭제
                  </button>
                  &nbsp; &nbsp;
                  {/* <button className="edit-button">수정</button>{' '} */}
                  <button
                    className="btn btn-square btn-secondary active btn-sm"
                    type="button"
                    onClick={onSubmithandler}
                  >
                    작성
                  </button>
                </div>
              </form>
            </div>
            {/* <div className="col-sm-12">
              <div> */}
            {/* </div>
            </div> */}
            {/* <div className="col-sm-12"> */}
            {/* </div>
            <div className="col-sm-12">
              <div> */}
          </div>
        </div>
      </div>
      {/* </div>
      </div> */}
    </>
  )
}

export default AddBenner
