import React, { useContext, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import Data from '../Data';
import { Modal, Button } from 'react-bootstrap';
import GlobalContext from '../context/global.context';

const Userdetail = () => {
  // const Datacontext = React.createContext();
  // const 데이터 = useContext(Datacontext);

  const { testData, onChangeGlobal } = useContext(GlobalContext);
  console.log(testData);

  const history = useHistory();
  let { id } = useParams();
  const [modal, setmodal] = useState(0);
  return (
    <div>
      <div className="card-block row">
        <div onClick={() => onChangeGlobal({ testData: 'value' })}>btn</div>
        <div className="col-sm-12 col-lg-12 col-xl-12" id="bennerH">
          <div className="bennerContent">
            <div className="benner">유저상세보기</div>
            <div className="benner">
              <div className="benner-btn">
                <button
                  id="bnr-btn"
                  className="btn btn-danger btn-sm"
                  onClick={() => {
                    setmodal(1);
                  }}
                >
                  탈퇴
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="card">
        <div className="card-header">
          <h5>상세보기</h5>
        </div>
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12" id="bennerH">
            <div className="test-wrapper">
              <div className="left">
                <div>가입일시</div>
                <div>소셜</div>
                <div>언어</div>
                <div>이메일</div>
                <div>닉네임</div>
              </div>
              <div className="right">
                <div>{Data[id - 1].datea}</div>
                <div>{Data[id - 1].sns}</div>
                <div>{Data[id - 1].lang}</div>
                <div>{Data[id - 1].email}</div>
                <div>{Data[id - 1].nicname}</div>
              </div>
            </div>
            <Modalal modal={modal} />
          </div>
        </div>
      </div>
    </div>
  );
};

const SecessionModal = () => {
  const [btn, setbtn] = useState(false);
  return (
    <div>
      <Modal.Dialog>
        <Modal.Header closeButton>
          <Modal.Title>탈퇴여부</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <p>탈퇴처리시 유저데이터가 모두사라집니다 탈퇴처리하시겠습니까?</p>
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary" onClick={setbtn(!btn)}>
            확인
          </Button>
          <Button variant="primary">취소</Button>
        </Modal.Footer>
      </Modal.Dialog>
    </div>
  );
};
const OkModal = () => {
  return (
    <div>
      <Modal.Dialog>
        <Modal.Header closeButton>
          <Modal.Title>탈퇴여부</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <p>탈퇴처리시 유저데이터가 모두사라집니다 탈퇴처리되었습니다</p>
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary">확인</Button>
        </Modal.Footer>
      </Modal.Dialog>
    </div>
  );
};
function Modalal(props) {
  if (props.modal === 0) {
    return <div></div>;
  } else if (props.modal === 1) {
    return (
      <div>
        <SecessionModal props={props} />
      </div>
    );
  } else if (props.modal === 2) {
    return (
      <div>
        <OkModal />
      </div>
    );
  }
}
export default Userdetail;
