import React, { useContext, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import Data from '../Data';
import { Modal, Button } from 'react-bootstrap';
import GlobalContext from '../context/global.context';
import Breadcrumb from '../components/common/breadcrumb';
const Userdetail = () => {
  // const Datacontext = React.createContext();
  // const 데이터 = useContext(Datacontext);

  const { testData, onChangeGlobal } = useContext(GlobalContext);
  console.log(testData);

  const history = useHistory();
  let { id } = useParams();
  const [modal, setmodal] = useState(0);

  return (
    <div>
      <div className="card-block row">
        <div onClick={() => onChangeGlobal({ testData: 'value' })}>btn</div>
        <div className="col-sm-12 col-lg-12 col-xl-12" id="bennerH"></div>
      </div>
      <div className="card">
        <div className="bennerContent">
          <h5 className="benner">유저상세보기</h5>
          <div className="benner">
            <div className="benner-btn">
              <button
                id="bnr-btn"
                className="btn btn-danger btn-sm"
                onClick={() => {
                  setmodal(1);
                }}
              >
                탈퇴
              </button>
            </div>
          </div>
        </div>
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12" id="bennerH">
            {modal === 1 && (
              <Modal.Dialog>
                <Modal.Header closeButton>
                  <Modal.Title>탈퇴여부</Modal.Title>
                </Modal.Header>

                <Modal.Body>
                  <p>
                    탈퇴처리시 유저데이터가 모두사라집니다 탈퇴처리하시겠습니까?
                  </p>
                </Modal.Body>

                <Modal.Footer>
                  <Button
                    variant="secondary"
                    onClick={() => {
                      setmodal(2);
                    }}
                  >
                    확인
                  </Button>
                  <Button variant="primary">취소</Button>
                </Modal.Footer>
              </Modal.Dialog>
            )}
            {modal === 2 && <OkModal setmodal={setmodal} />}
            <div className="test-wrapper">
              <div className="left">
                <div>가입일시</div>
                <div>소셜</div>
                <div>언어</div>
                <div>이메일</div>
                <div>닉네임</div>
              </div>
              <div className="right">
                <div>{Data[id - 1].datea}</div>
                <div>{Data[id - 1].sns}</div>
                <div>{Data[id - 1].lang}</div>
                <div>{Data[id - 1].email}</div>
                <div>{Data[id - 1].nicname}</div>
              </div>
            </div>
            <div className="card-header">
              <h5>이용내역</h5>
            </div>
            <div className="table-responsive">
              <table className="table">
                <thead className="thead-light">
                  <tr>
                    <th scope="col">결제일자</th>
                    <th scope="col">이용일자</th>
                    <th scope="col">타입</th>
                    <th scope="col">상세정보</th>
                    <th scope="col">상품금액</th>
                    <th scope="col">결제금액</th>
                    <th scope="col">비고</th>
                  </tr>
                </thead>
                <tbody>
                  {Data.map((a) => {
                    return (
                      <tr>
                        <th scope="row" key={a.id}>
                          {a.id}
                        </th>
                        <td>{a.datea}</td>
                        <td>{a.sns}</td>
                        <td>{a.lang}</td>
                        <td>{a.email}</td>
                        <td>{a.nicname}</td>
                        <td></td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const OkModal = (porps) => {
  return (
    <div>
      <Modal.Dialog>
        <Modal.Header closeButton>
          <Modal.Title>탈퇴여부</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <p>탈퇴처리시 유저데이터가 모두사라집니다 탈퇴처리되었습니다</p>
        </Modal.Body>

        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => {
              porps.setmodal(0);
            }}
          >
            확인
          </Button>
        </Modal.Footer>
      </Modal.Dialog>
    </div>
  );
};

export default Userdetail;
