import React, { useState, useEffect } from "react"
import { useHistory } from "react-router-dom"
import "../assets/css/layout.css"
import { Nav } from "react-bootstrap"
import Breadcrumb from "../components/common/breadcrumb"
import PagnationTool from "../PagnationTool"
import one from "../assets/images/user/1.jpg"
import axios from "axios"
import Cookie from "js-cookie"
const dummyData = [
  {
    id: "1",
    facility: "1",
    visitor: "2020.05.14",
    sortation: "이미지",
    phone: "010-****-***9",
    date: "2020.05.14",
    Validity: "30분",
    Approval: "승인"
  },
  {
    id: "2",
    facility: "99",
    visitor: "2020.05.14",
    sortation: "이미지",
    phone: "010-****-***1",
    date: "2020.08.14",
    Validity: "35분",
    Approval: "거절"
  },
  {
    id: "3",
    facility: "80",
    visitor: "2020.05.14",
    sortation: "이미지",
    phone: "010-****-***3",
    date: "2020.07.14",
    Validity: "30분",
    Approval: "승인"
  }
]
const RecomendList = () => {
  const history = useHistory()
  // const [en, seten] = useState(false);
  // const [zn, setzn] = useState(false);
  // const 영어관리스위치 = () => {
  //   seten(!en);
  // };
  let [누른탭, 누른탭변경] = useState(0)
  return (
    <>
      <Breadcrumb title="추천 가이드관리" parent="추천 가이드리스트" />
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm-12">
            <div className="card">
              <div className="card-header">
                <div className="bennerContent">
                  <h5>추천 가이드리스트</h5>
                </div>

                <div className="card-body">
                  <div className="nav-sub">
                    <Nav
                      variant="tabs"
                      defaultActiveKey="link-0"
                      className="nav--sub2"
                    >
                      <div className="navtab">
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-0"
                            onClick={() => {
                              누른탭변경(0)
                            }}
                          >
                            영문관리
                          </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                          <Nav.Link
                            eventKey="link-1"
                            onClick={() => {
                              누른탭변경(1)
                            }}
                          >
                            중문관리
                          </Nav.Link>
                        </Nav.Item>
                      </div>
                      <div>
                        <button
                          id="maching-btn"
                          className="btn btn-danger btn-sm"
                          onClick={() => {
                            history.push("/recomendguidecnt")
                          }}
                        >
                          상품연결
                        </button>
                      </div>
                    </Nav>
                    <TabContent 누른탭={누른탭} />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* <div className="card-block row">
        <div className="col-sm-12 col-lg-12 col-xl-12"></div>
      </div> */}
    </>
  )
}
function TabContent(props) {
  if (props.누른탭 === 0) {
    return <WaitGuide />
  } else if (props.누른탭 === 1) {
    return <Okayguide />
  }
}

const WaitGuide = () => {
  const [waitguide, setwaitguide] = useState([])
  useEffect(() => {
    let form = new FormData()
    form.append("index", 1)
    form.append("type", 1)
    axios
      .post("/api/v1/guide/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        console.log(response)
      })
    return () => {}
  }, [])
  return (
    <div className="table-responsive">
      <table className="table">
        <thead className="thead-light">
          <tr>
            <th scope="col" />
            <th scope="col">노출순위</th>
            <th scope="col">작성일시</th>
            <th scope="col">가이드정보</th>
            <th scope="col" />
          </tr>
        </thead>
        <tbody>
          {dummyData.map(a => {
            return (
              <tr>
                <th scope="row" key={a.id}>
                  {a.id}
                </th>
                <td>{a.facility}</td>
                <td>{a.date}</td>
                <td>
                  <img className="img-50 b-r-15" src={one} alt="#" />
                  가이드명/나이/영어,중국어/위치,평점
                </td>
                <td>
                  <button
                    className="btn btn-square btn-warning btn-xs"
                    type="button"
                  >
                    삭제
                  </button>
                  &nbsp; &nbsp;
                  {/* <button className="edit-button">수정</button>{' '} */}
                  <button
                    className="btn btn-square btn-secondary active btn-xs"
                    type="button"
                  >
                    수정
                  </button>
                </td>
              </tr>
            )
          })}
        </tbody>
      </table>
      <PagnationTool />
    </div>
  )
}
const Okayguide = () => {
  return (
    <div className="table-responsive">
      <table className="table">
        <thead className="thead-light">
          <tr>
            <th scope="col" />
            <th scope="col">노출순위</th>
            <th scope="col">작성일시</th>
            <th scope="col">가이드정보 </th>
            <th scope="col" />
          </tr>
        </thead>
        <tbody>
          {dummyData.map(a => {
            return (
              <tr>
                <th scope="row" key={a.id}>
                  {a.id}
                </th>

                <td>{a.facility}</td>
                <td>{a.date}</td>
                <td>
                  <img className="img-50 b-r-15" src={one} alt="#" />
                  가이드명/나이/영어,중국어/위치,평점
                </td>
                <td>
