import React, { useContext, useState, useEffect } from "react"
import { useHistory } from "react-router-dom"
import "../assets/css/layout.css"
import NewCard from "../components/common/NewCard"
import PagnationTool from "../PagnationTool"
import Lightbox from "react-image-lightbox"
import "react-image-lightbox/style.css"
import axios from "axios"
import Cookie from "js-cookie"

const KoreaNewsUserList = () => {
  const history = useHistory()
  const [isOpen, setIsOpen] = useState(false)
  const [photoIndex, setPhotoIndex] = useState(0)
  const [newsList, setNewsList] = useState([])
  useEffect(() => {
    let form = new FormData()
    form.append("index", 1)

    axios
      .post("/api/v1/news/list", form, {
        headers: {
          Authorization: Cookie.get("token")
        }
      })
      .then(response => {
        console.log(response)
        if (response.data.status === "success") {
          setNewsList(response.data.data.list)
        }
      })
      .catch(err => {
        alert("한국소식을  불러오는데 실패했습니다.")
      })
  }, [])
  // let 데이터 = useContext(데이터context);
