import React, { useState } from "react"
import "../assets/css/layout.css"

import Breadcrumb from "../components/common/breadcrumb"
import axios from "axios"
import CKEditors from "react-ckeditor-component"
import Cookie from "js-cookie"
const AddNotiGuide = () => {
  const [KoTitle, setKoTitle] = useState("")
  const [KoContent, setKoContent] = useState("")

  const onKoContentHandler = (e) => {
    const content = e.editor.getData()

    setKoContent(content)
  }
  const onSubmithandler = (e) => {
    e.preventDefault()
    let form = new FormData()
    form.append("title", KoTitle)
    form.append("editor", KoContent)

    const cookie = Cookie.get("token")
    axios.post("/api/v1/notice/addG", form, {
      headers: {
        Authorization: cookie,
      },
    })
  }
  const KoText = () => {
    // console.log("????")
    return (
      <div className="col-sm-12">
        <div className="card">
          <div className="card-header">
            <h5>영문내용입력하기</h5>
          </div>
          <div className="card-body">
            <CKEditors
              editor={CKEditors}
              content={KoContent}
              events={{
                change: onKoContentHandler,
              }}
            />
          </div>
        </div>
      </div>
    )
  }

  return (
    <>
      <Breadcrumb title="가이드용 공지사항" parent="공지사항관리" />
      <div className="card">
        <div className="card-block row">
          <div className="col-sm-12 col-lg-12 col-xl-12">
            <div className="card-body">
              <div className="noti-title">
                <form>
                  <h5>제목입력</h5>
                  <input
                    onChange={(e) => setKoTitle(e.target.value)}
                    value={KoTitle}
                    class="form-control"
                    id="exampleFormControlInput1"
                    type="input"
                    placeholder="한글로입력해주세요"
                  ></input>
                </form>
              </div>
            </div>
            <div className="col-sm-12">
              <div></div>
            </div>
            <div className="col-sm-12">
              <div>{KoText()}</div>
            </div>
            <div className="col-sm-12">
              <div>
                <div>
                  <button
                    className="btn btn-square btn-warning btn-sm"
                    type="button"
                  >
                    삭제
                  </button>
                  &nbsp; &nbsp;
                  {/* <button className="edit-button">수정</button>{' '} */}
                  <button
                    className="btn btn-square btn-secondary active btn-sm"
                    type="button"
                    onClick={onSubmithandler}
                  >
                    작성
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default AddNotiGuide
